import type { GameTranslation } from '../gameTranslations';

/** French game translations */
export const FR_GAMES: Record<string, GameTranslation> = {
  baseball: {
    title: 'Baseball — Google Doodle',
    description:
      `Jouez au jeu de baseball Google Doodle directement dans votre navigateur. Frappez des home runs et battez les records dans ce jeu rétro amusant.\n\nPublié à l'origine le 4 juillet 2019 pour célébrer la fête nationale américaine et les barbecues estivaux, ce Doodle met en scène des en-cas typiques des stades de baseball. Créé en collaboration avec le studio d'animation japonais STUDIO4°C, il reste l'un des Doodles sportifs les plus rejoués de l'histoire de Google.`,
    controls: 'Cliquez ou appuyez pour frapper la balle. Observez le lanceur se préparer et cliquez au bon moment pour établir le contact. Visez le point idéal pour réussir des coups de circuit. Vous avez trois retraits par manche — chronométrez vos frappes avec soin pour marquer le plus de points possible.',
  },
  'doodle-cricket-game': {
    title: 'Jeu de Cricket Doodle',
    description:
      `Profitez du jeu de cricket Google Doodle en ligne gratuitement. Marquez des points et devenez le champion du cricket dans ce mini-jeu addictif.\n\nLancé le 18 juin 2017 pour coïncider avec le Trophée des Champions ICC, ce Doodle met en scène d'adorables insectes — escargots, papillons et coccinelles — pour rendre le cricket accessible au monde entier. Il est devenu l'un des Doodles interactifs les plus viraux, cumulant des millions de parties dès sa première semaine.`,
    controls: 'Cliquez ou appuyez pour frapper quand la balle est lancée. Le timing est primordial — frapper trop tôt ou trop tard vous fera rater. Frappez la balle pour marquer des runs. Le jeu se termine quand tous vos guichets sont pris. Essayez de marquer le plus de runs possible avant d\'être éliminé.',
  },
  snake: {
    title: 'Google Snake — Jeu du Serpent',
    description:
      `Jouez au célèbre jeu du serpent de Google. Mangez les pommes, grandissez et évitez de vous mordre la queue dans ce classique indémodable.\n\nLes origines du jeu du serpent remontent à la fin des années 1970, mais c'est Nokia qui l'a popularisé en le préinstallant sur ses téléphones en 1998. La version navigateur de Google capture fidèlement cette simplicité addictive tout en ajoutant des graphismes colorés et des animations fluides, prouvant qu'un gameplay intemporel ne se démode jamais.`,
    controls: 'Utilisez les touches fléchées ou WASD pour diriger votre serpent. Mangez les fruits qui apparaissent sur le plateau pour grandir et marquer des points. Évitez de heurter les murs ou votre propre queue — les deux mettent fin à la partie. Le serpent avance en continu, alors planifiez votre trajectoire. Sur mobile, glissez dans la direction souhaitée.',
  },
  pacman: {
    title: 'Google Pac-Man',
    description:
      `Revivez le classique Pac-Man dans la version Google Doodle. Dévorez les points, esquivez les fantômes et visez le meilleur score.\n\nPublié le 21 mai 2010 pour le 30e anniversaire de Pac-Man, ce fut le tout premier Doodle jouable de la page d'accueil de Google. Il aurait coûté au monde 120 millions de dollars en productivité perdue le jour de son lancement. Le mode deux joueurs caché, activé en cliquant deux fois sur « Insert Coin », fait apparaître Ms. Pac-Man contrôlée avec les touches WASD.`,
    controls: 'Utilisez les touches fléchées pour déplacer Pac-Man dans le labyrinthe. Mangez tous les petits points pour compléter le niveau. Évitez les quatre fantômes — en toucher un coûte une vie. Mangez une grosse Pastille de Pouvoir pour rendre temporairement les fantômes bleus, ce qui vous permet de les manger pour des points bonus. En mode deux joueurs, le Joueur 2 utilise W/A/S/D.',
  },
  'champion-island-games': {
    title: 'Google Doodle — Jeux de Champion Island',
    description:
      `Explorez l'île Champion dans ce jeu d'aventure RPG de Google. Affrontez des champions légendaires dans sept mini-jeux sportifs épiques.\n\nPublié le 23 juillet 2021 pour coïncider avec la cérémonie d'ouverture des Jeux olympiques de Tokyo, c'est l'un des Google Doodles les plus ambitieux jamais créés. Développé avec le studio d'animation japonais STUDIO4°C, il propose un monde richement détaillé inspiré de la mythologie japonaise, avec plus de sept heures de contenu et des quêtes secondaires cachées.`,
    controls: 'Utilisez les touches fléchées pour déplacer Lucky sur l\'île. Appuyez sur Z ou cliquez pour interagir avec les personnages et entrer dans les mini-jeux. Chaque sport a des contrôles uniques affichés à l\'écran — par exemple, appuyez sur la barre d\'espace en rythme pour le marathon, ou cliquez pour viser et relâcher au tir à l\'arc. Explorez toute l\'île pour trouver les sept champions.',
  },
  'magic-cat-academy': {
    title: 'Académie des Chats Magiques',
    description:
      `Aidez Momo le chat magique à vaincre les fantômes en dessinant des symboles. Un jeu Halloween Google Doodle captivant et adorable.\n\nPublié le 30 octobre 2016, ce Doodle met en vedette Momo, dont le design est inspiré d'un vrai chat noir appartenant à un membre de l'équipe Google Doodle. Les cinq sorts à base de gestes deviennent diaboliquement difficiles à tracer à haute vitesse. Une suite très appréciée, Magic Cat Academy 2, a suivi en 2020 avec un décor sous-marin.`,
    controls: 'Cliquez et faites glisser la souris (ou glissez sur mobile) pour dessiner le symbole affiché au-dessus de chaque fantôme qui approche. Tracez la forme avec précision pour bannir le fantôme avant qu\'il n\'atteigne Momo. Les fantômes deviennent plus rapides et les symboles plus complexes à mesure que vous progressez, alors restez attentif et dessinez vite.',
  },
  'garden-gnomes': {
    title: 'Nains de Jardin — Google Doodle',
    description:
      `Lancez des nains de jardin le plus loin possible dans ce jeu Google Doodle amusant. Visez bien et battez votre record de distance.\n\nCe Doodle est apparu le 10 juin 2018, célébrant la tradition des nains de jardin qui remonte à l'Allemagne du XIXe siècle, où l'on croyait qu'ils portaient bonheur. Le moteur physique qui régit chaque lancement a été soigneusement calibré pour rendre chaque tentative satisfaisante et imprévisible. Son style artistique joyeux en fait un favori des joueurs occasionnels.`,
    controls: 'Cliquez ou appuyez sur Espace pour lancer le gnome depuis la catapulte. Le bras pivote automatiquement — relâchez au bon angle pour la distance maximale. Une fois en l\'air, cliquez à nouveau pour activer un boost. Rebondissez sur les champignons et objets pour aller plus loin. Votre objectif est de lancer le gnome le plus loin possible.',
  },
  minesweeper: {
    title: 'Google Démineur',
    description:
      `Jouez au démineur classique dans la version Google. Dévoilez les cases, évitez les mines et testez votre logique dans ce jeu de réflexion.\n\nLe Démineur est apparu pour la première fois sur Windows 3.1 en 1990 et avait été conçu à l'origine pour apprendre aux utilisateurs à maîtriser les clics gauche et droit de la souris. La version moderne de Google conserve la logique satisfaisante de l'original tout en ajoutant des visuels épurés et des contrôles tactiles réactifs pour le jeu sur mobile.`,
    controls: 'Faites un clic gauche sur une case pour la révéler. Les chiffres indiquent combien de mines sont adjacentes. Faites un clic droit (ou appui long sur mobile) pour placer un drapeau sur une case suspecte. Utilisez les chiffres pour déduire les emplacements sûrs. Révélez toutes les cases sans mines pour gagner — mais cliquer sur une mine met fin à la partie.',
  },
  'tic-tac-toe': {
    title: 'Google Morpion',
    description:
      `Défiez l'intelligence artificielle de Google au morpion. Alignez trois symboles pour gagner dans ce jeu de stratégie simple mais addictif.\n\nLe morpion est joué depuis des millénaires, avec des traces de jeux similaires retrouvées dans l'Égypte antique datant de 1300 av. J.-C. Mathématiquement, il existe 255 168 séquences de jeu possibles mais seulement trois issues. L'IA « Impossible » de Google utilise une stratégie minimax parfaite qu'il est impossible de battre — le meilleur résultat possible est le match nul.`,
    controls: 'Cliquez sur n\'importe quelle case vide de la grille 3×3 pour placer votre symbole (X ou O). Alternez les tours avec votre adversaire. Le premier joueur à aligner trois symboles — horizontalement, verticalement ou en diagonale — gagne. Si les neuf cases sont remplies sans vainqueur, c\'est un match nul.',
  },
  feud: {
    title: 'Google Feud',
    description:
      `Devinez les suggestions de recherche Google les plus populaires. Testez vos connaissances dans ce jeu de devinettes hilarant et surprenant.\n\nInspiré de l'émission télévisée Family Feud, Google Feud utilise de véritables données d'autocomplétion, faisant de chaque manche une fenêtre fascinante sur la curiosité collective. Le jeu puise dans quatre catégories — Culture, Personnes, Noms et Questions — offrant des types de recherches très variés dont certaines réponses sont hilarantes et inattendues.`,
    controls: 'Tapez votre réponse dans la zone de texte et appuyez sur Entrée pour soumettre. Vous essayez de deviner les suggestions d\'autocomplétion les plus courantes de Google pour la phrase donnée. Les meilleures réponses valent le plus de points. Vous avez un nombre limité de tentatives par manche, alors pensez d\'abord aux réponses les plus populaires.',
  },
  halloween: {
    title: 'Google Doodle Halloween',
    description:
      `Célébrez Halloween avec ce jeu Google Doodle spécial. Affrontez des créatures effrayantes dans une aventure festive et amusante.\n\nLe Grand Duel des Fantômes a été publié le 30 octobre 2018 et est devenu le tout premier Google Doodle multijoueur, permettant à jusqu'à huit joueurs de s'affronter en temps réel. Les designs colorés des fantômes et le gameplay frénétique de vol de flammes s'inspirent des jeux de fête multijoueurs classiques. Une version remaniée a suivi en 2021.`,
    controls: 'Utilisez les touches fléchées ou WASD pour déplacer votre fantôme sur la carte hantée. Marchez sur les flammes spirituelles pour les collecter, puis retournez au chaudron de votre équipe pour les déposer. Foncez dans les fantômes ennemis pour leur voler leur butin. L\'équipe avec le plus de flammes à la fin du temps gagne.',
  },
  'boba-bubble-tea': {
    title: 'Jeu du Bubble Tea Google',
    description:
      `Préparez du bubble tea dans ce jeu Google Doodle interactif. Mélangez les ingrédients et créez la boisson parfaite.\n\nCe Doodle a été publié le 30 janvier 2024 pour célébrer l'impact culturel du bubble tea, après que Taïwan l'a officiellement reconnu comme un élément important de son patrimoine culinaire. Les histoires de saveurs générées par apprentissage automatique suivent de vrais principes d'accord de thé. Avec des milliers de combinaisons possibles, aucune création de boba ne raconte la même histoire.`,
    controls: 'Cliquez ou appuyez sur chaque option d\'ingrédient pour personnaliser votre boba. Progressez à travers chaque étape : choisissez la base de thé, le type de lait, le niveau de sucre, la quantité de glace et enfin vos garnitures. Après chaque sélection, l\'option suivante apparaît. Terminez votre boisson et partagez votre création personnalisée.',
  },
  'google-cat-game': {
    title: 'Google Cat Game — Jeu du Chat',
    description:
      `Jouez avec le chat de Google dans ce mini-jeu adorable. Un Doodle interactif mignon et divertissant pour tous les amoureux des chats.\n\nC'est le Doodle Halloween original de 2016, Magic Cat Academy, qui a présenté Momo le chat sorcier au monde pour la première fois. Les joueurs doivent tracer des symboles de plus en plus complexes, ce qui en fait un test de dextérité étonnamment intense. Les cinq niveaux sont thématisés autour de différentes zones d'une école hantée, culminant en un combat de boss final.`,
    controls: 'Cliquez et faites glisser pour dessiner le symbole au-dessus de chaque fantôme avant qu\'il n\'atteigne Momo. Les nouveaux types de fantômes nécessitent des symboles plus complexes — faites bien attention à la forme affichée. Certains fantômes se déplacent plus vite, alors priorisez les menaces les plus proches. Dessinez rapidement et avec précision pour protéger Momo.',
  },
  'no-internet-dinosaur-game-google-chrome-dino': {
    title: 'Jeu du Dinosaure Google Chrome (Dino hors connexion)',
    description:
      `Le fameux jeu du dinosaure T-Rex de Chrome, jouable même en ligne. Sautez par-dessus les cactus et battez votre meilleur score.\n\nCréé par le designer UX de Chrome Sebastien Gabriel en 2014, le jeu du T-Rex a été conçu comme un clin d'œil ludique à l'ère préhistorique de l'absence d'internet. Le jeu dispose d'un mode nuit caché qui s'active à 700 points et d'un score maximal de 99 999. Il cumule environ 270 millions de parties par mois, en faisant l'un des jeux les plus joués au monde.`,
    controls: 'Appuyez sur Espace ou Flèche Haut pour sauter par-dessus les cactus. Appuyez sur Flèche Bas pour vous baisser sous les ptérodactyles. Sur mobile, touchez l\'écran pour sauter. Le jeu commence lentement et accélère continuellement à mesure que votre score augmente. Il n\'y a pas de fin — survivez le plus longtemps possible pour le meilleur score.',
  },
  't-rex-run-3d': {
    title: 'T-Rex Run 3D — Course du Dinosaure 3D',
    description:
      `Découvrez le jeu du dinosaure Chrome en version 3D. Courez, esquivez les obstacles et survivez le plus longtemps possible.\n\nCette réimagination 3D créée par des fans transforme le jeu original en défilement latéral en une perspective immersive derrière le dinosaure, avec un terrain texturé et des nuages volumétriques. Le défi de perception de la profondeur oblige les joueurs à juger les distances en trois dimensions. Malgré son caractère non officiel, il a acquis un énorme succès comme l'un des meilleurs mods du Chrome Dino.`,
    controls: 'Appuyez sur Espace ou Flèche Haut pour sauter par-dessus les cactus et obstacles. Appuyez sur Flèche Bas pour vous baisser. En mode 3D, les obstacles arrivent depuis l\'horizon — évaluez bien la distance avant de sauter. Le jeu accélère progressivement. Visez le meilleur score possible.',
  },
  'google-santa-tracker': {
    title: 'Google Santa Tracker — Suivez le Père Noël',
    description:
      `Suivez le Père Noël dans sa tournée mondiale avec Google Santa Tracker. Jouez à des mini-jeux festifs et célébrez Noël.\n\nLe Santa Tracker de Google est une tradition de Noël depuis 2004, évoluant d'un simple suivi sur carte en une expérience festive complète avec des dizaines d'activités. Le village s'enrichit chaque année de nouveaux mini-jeux, de tutoriels de codage et de contenu éducatif sur les traditions de Noël à travers le monde. Le soir de Noël, le suivi passe en mode direct.`,
    controls: 'Cliquez sur les bâtiments et personnages du village festif pour lancer différents mini-jeux et activités. Chaque mini-jeu a ses propres contrôles affichés à l\'écran. Utilisez la souris pour naviguer sur la carte et explorer tout ce que le village du Père Noël a à offrir.',
  },
  'blob-opera-google-game': {
    title: 'Blob Opera — Jeu Google',
    description:
      `Créez de la musique d'opéra avec des blobs colorés dans ce jeu Google interactif. Composez des mélodies uniques et amusantes.\n\nCréé par l'artiste David Li en collaboration avec Google Arts & Culture, Blob Opera utilise un modèle d'apprentissage automatique entraîné sur quatre vrais chanteurs d'opéra — un ténor, une basse, une mezzo-soprano et une soprano. L'IA a appris à harmoniser en étudiant plus de 100 heures de performances vocales. Publié le 15 décembre 2020, il est rapidement devenu une sensation virale.`,
    controls: 'Cliquez et faites glisser n\'importe quel blob vers le haut ou le bas pour changer sa hauteur de note — plus haut signifie une note plus aiguë. Glissez à gauche ou à droite pour changer le son vocal du blob. Les quatre blobs s\'harmonisent automatiquement en temps réel grâce à l\'apprentissage automatique. Expérimentez différentes combinaisons pour créer de belles pièces d\'opéra.',
  },
  'space-invaders-google': {
    title: 'Space Invaders de Google',
    description:
      `Défendez la Terre contre les envahisseurs de l'espace dans la version Google de Space Invaders. Un classique de l'arcade revisité.\n\nL'original Space Invaders a été créé par Tomohiro Nishikado en 1978 et est devenu si populaire au Japon qu'il a provoqué une pénurie nationale de pièces de monnaie. Le jeu a introduit de nombreuses premières dans l'industrie de l'arcade, notamment un système de meilleur score et une difficulté progressive. L'hommage de Google recrée fidèlement la tension de voir les rangées d'envahisseurs avancer inexorablement.`,
    controls: 'Utilisez les flèches gauche et droite pour déplacer votre canon. Appuyez sur Espace pour tirer. Éliminez tous les envahisseurs avant qu\'ils n\'atteignent le bas de l\'écran. Abritez-vous derrière les barrières destructibles — mais sachez que les tirs ennemis les érodent. Les envahisseurs accélèrent à mesure que leur nombre diminue.',
  },
  'celebrating-petanque': {
    title: 'La Pétanque à l\'honneur',
    description:
      `Célébrez la pétanque avec ce Google Doodle. Lancez vos boules au plus près du cochonnet dans ce jeu de précision typiquement français.\n\nCe Doodle célèbre la riche tradition de la pétanque, née en Provence en 1907, devenue depuis un passe-temps mondial pratiqué dans les parcs et sur les places. La simulation physique modélise avec précision le poids et le roulement des boules métalliques sur le gravier. Les joueurs stratégiques maîtrisent à la fois la technique du « pointer » pour les placements et celle du « tirer » pour déloger les adversaires.`,
    controls: 'Cliquez et maintenez pour viser votre lancer, puis faites glisser pour régler la puissance. Relâchez pour lancer votre boule vers le cochonnet (la petite balle cible). Essayez de placer votre boule plus près du cochonnet que celle de votre adversaire. Vous pouvez stratégiquement frapper les boules adverses pour les éloigner. Le joueur le plus proche du cochonnet remporte la manche.',
  },
  'dino-swords': {
    title: 'Dino Swords — Épées de Dinosaure',
    description:
      `Jouez au jeu du dinosaure Chrome avec des armes. Utilisez épées, fusils et boucliers pour anéantir les obstacles sur votre chemin.\n\nDino Swords a été créé par MSCHF, le collectif créatif à l'origine de nombreux projets viraux sur internet, comme une parodie affectueuse du jeu hors connexion de Chrome. Le mod propose plus de 26 armes différentes — dont des nunchakus, un sabre laser et un pistolet gravitationnel — chacune avec des animations et des mécaniques uniques. Il est devenu viral sur les réseaux sociaux, prouvant que même les jeux les plus simples peuvent être brillamment réinventés.`,
    controls: 'Appuyez sur Espace ou Flèche Haut pour sauter. Appuyez sur Flèche Bas pour vous baisser. Votre arme actuelle s\'active automatiquement quand les ennemis sont à portée. Ramassez les bonus d\'armes qui apparaissent pendant la course pour changer d\'équipement. Chaque arme a une portée et des dégâts différents — expérimentez pour trouver votre favorite.',
  },
  'doodle-scoville': {
    title: 'Doodle Scoville — Échelle du Piment',
    description:
      `Découvrez l'échelle de Scoville avec ce Google Doodle interactif. Lancez des boules de glace sur des piments dans ce jeu épicé.\n\nCe Doodle a été publié le 22 janvier 2016 pour célébrer le 151e anniversaire du pharmacien Wilbur Scoville, qui a développé le test organoleptique de Scoville en 1912 pour mesurer le piquant des piments. Le jeu présente de vrais piments classés selon leurs unités Scoville réelles — du poivron doux à zéro SHU jusqu'au redoutable Carolina Reaper à plus de deux millions. Les patterns d'attaque de chaque boss reflètent astucieusement leur niveau de piquant réel.`,
    controls: 'Cliquez ou appuyez pour lancer des boules de glace contre le piment rival. Déplacez votre piment à gauche et à droite avec la souris ou les flèches. Esquivez les attaques de l\'adversaire tout en touchant les vôtres. Chaque niveau amène des piments plus forts avec des attaques plus puissantes. Battez tous les rivaux pour devenir le champion du piquant.',
  },
  'doodle-valentines-day': {
    title: 'Doodle Saint-Valentin',
    description:
      `Célébrez l'amour avec ce jeu Google Doodle de la Saint-Valentin. Aidez les créatures à trouver leur âme sœur dans ce jeu romantique.\n\nPublié le 14 février 2017, ce Doodle met en vedette le pangolin — l'un des animaux les plus menacés au monde — pour sensibiliser à la conservation de la faune aux côtés de sa thématique romantique. Les quatre niveaux représentent chacun un écosystème différent, des forêts tropicales aux déserts, mettant en lumière les habitats naturels du pangolin. Google s'est associé à des organisations de conservation pour attirer l'attention mondiale sur le sort de ces créatures fragiles.`,
    controls: 'Cliquez ou appuyez pour interagir avec la scène et guider le pangolin dans son voyage de livraison. Cliquez sur les objets et personnages en surbrillance pour déclencher des animations et faire avancer l\'histoire. Le jeu est principalement narratif — détendez-vous et profitez du charmant voyage animé.',
  },
  'pony-express': {
    title: 'Pony Express — Courrier à Cheval',
    description:
      `Galopez à travers le Far West dans ce Google Doodle Pony Express. Collectez le courrier et évitez les obstacles à cheval.\n\nPublié le 14 avril 2015, ce Doodle commémore le 155e anniversaire du Pony Express, qui n'a fonctionné que 18 mois en 1860–1861 mais est devenu l'une des légendes frontalières les plus durables de l'Amérique. Les cinq étapes du jeu suivent le véritable itinéraire postal de St. Joseph, Missouri, à Sacramento, Californie — un voyage éprouvant de 3 000 km que les cavaliers accomplissaient en seulement 10 jours.`,
    controls: 'Appuyez sur Espace ou cliquez pour faire sauter votre cheval par-dessus les obstacles comme les rochers, clôtures et rivières. Ramassez les sacs de courrier qui apparaissent le long du parcours pour des points bonus. Votre cheval court automatiquement — concentrez-vous sur le timing des sauts pour éviter les dangers et livrer le courrier.',
  },
  'doodle-jump-2': {
    title: 'Doodle Jump 2 — Le Saut du Gribouillis',
    description:
      `Sautez de plateforme en plateforme dans Doodle Jump 2. Montez toujours plus haut et évitez les pièges dans ce jeu de saut addictif.\n\nLe Doodle Jump original, lancé en 2009, est devenu l'un des jeux mobiles les plus vendus de tous les temps, trônant pendant des mois en tête des classements de l'App Store. Cette suite enrichit la formule adorée avec de multiples mondes thématiques, chacun proposant des dangers et des power-ups uniques. Le style graphique dessiné à la main qui a rendu l'original si charmant a été soigneusement préservé tout en ajoutant une variété visuelle considérable.`,
    controls: 'Inclinez votre appareil ou utilisez les flèches gauche/droite pour déplacer le Doodler latéralement. Le Doodler saute automatiquement en atterrissant sur les plateformes. Appuyez ou cliquez pour tirer sur les monstres au-dessus de vous. Les nouvelles mécaniques incluent des plateformes mobiles, des jetpacks et des trampolines qui modifient votre trajectoire.',
  },
  'doodle-history-of-pizza': {
    title: 'Doodle Histoire de la Pizza',
    description:
      `Découpez des pizzas dans ce Google Doodle interactif. Apprenez l'histoire de la pizza tout en jouant à ce jeu culinaire amusant.\n\nPublié le 6 décembre 2007, ce Doodle enseigne aux joueurs que l'histoire de la pizza remonte à des milliers d'années — des anciens Égyptiens cuisant des galettes plates jusqu'au moment légendaire de 1889 où le pizzaiolo napolitain Raffaele Esposito créa la pizza Margherita pour la reine Marguerite d'Italie. Les mini-jeux de chaque époque utilisent des mécaniques différentes qui reflètent habilement les techniques culinaires de la période.`,
    controls: 'Cliquez et faites glisser pour interagir à chaque étape de la fabrication de pizza. Pétrissez la pâte en cliquant rapidement, étalez la sauce en glissant et placez les garnitures en cliquant dessus. Chaque ère historique propose des mini-jeux légèrement différents reflétant les traditions de pizza de l\'époque.',
  },
  'doodle-kids-coding': {
    title: 'Doodle Codage pour Enfants',
    description:
      `Initiez-vous à la programmation avec ce Google Doodle éducatif. Guidez un lapin en assemblant des blocs de code ludiques.\n\nPublié le 4 décembre 2017, ce Doodle a été développé en partenariat avec l'équipe Scratch du MIT et s'inspire du langage de programmation Logo créé par Seymour Papert dans les années 1960. Ses six niveaux de difficulté croissante introduisent les séquences, les boucles et les conditions de manière accessible aux enfants dès quatre ans. Salué par les éducateurs du monde entier, il a été adopté comme outil pédagogique dans d'innombrables salles de classe.`,
    controls: 'Cliquez sur les blocs de commandes à gauche pour les ajouter à votre séquence. Arrangez les blocs dans le bon ordre pour guider le Lapin Doodle jusqu\'à la carotte. Cliquez sur le bouton Jouer pour exécuter votre programme et voir si le lapin atteint l\'objectif. Si ça ne marche pas, réorganisez les blocs et réessayez.',
  },
  'doodle-valentines-day-2022': {
    title: 'Doodle Saint-Valentin 2022',
    description:
      `Fêtez la Saint-Valentin 2022 avec ce Google Doodle spécial. Un mini-jeu romantique et charmant pour célébrer l'amour.\n\nLes Doodles de la Saint-Valentin de Google sont devenus une tradition annuelle depuis 2011, racontant chaque année une histoire d'amour unique à travers un prisme créatif différent. Le hérisson protagoniste de l'édition 2022 a été choisi car les vrais hérissons sont des créatures solitaires qui ne cherchent la compagnie qu'en période de reproduction, rendant la quête amoureuse particulièrement touchante. Le style aquarelle confère à chaque niveau une qualité de conte de fées.`,
    controls: 'Utilisez les touches fléchées ou WASD pour déplacer le hérisson à travers chaque niveau. Ramassez les fleurs et cœurs dispersés dans le décor. Évitez les obstacles et dangers qui bloquent votre chemin. Atteignez l\'objectif à la fin de chaque niveau pour faire avancer l\'histoire d\'amour.',
  },
  'doodle-ludwig-van-beethovens-245th-year': {
    title: 'Doodle — 245e anniversaire de Ludwig van Beethoven',
    description:
      `Aidez Beethoven à reconstituer ses partitions dans ce Google Doodle musical. Un jeu interactif rendant hommage au génie de la musique classique.\n\nPublié le 17 décembre 2015, ce Doodle présente quatre des compositions les plus emblématiques de Beethoven, dont la Cinquième Symphonie et la Lettre à Élise. Les scènes animées dépeignent la surdité progressive de Beethoven et sa détermination remarquable à continuer de composer malgré la perte totale de son audition à 44 ans. Le jeu enseigne habilement aux joueurs à reconnaître les phrases musicales à l'oreille.`,
    controls: 'Cliquez ou appuyez sur les pages de musique dispersées qui flottent à l\'écran pour les collecter. Les pages doivent être collectées dans le bon ordre pour reconstituer chaque composition — écoutez les indices musicaux pour déterminer la séquence. Complétez toutes les compositions pour aider Beethoven à restaurer son œuvre.',
  },
  'doodle-earth-day-2020': {
    title: 'Doodle Jour de la Terre 2020',
    description:
      `Célébrez le Jour de la Terre avec ce Google Doodle sur les abeilles. Pollinisez les fleurs et découvrez l'importance des abeilles pour la planète.\n\nLe Jour de la Terre a été célébré pour la première fois le 22 avril 1970, et ce Doodle de 2020 a marqué le 50e anniversaire avec un voyage interactif à travers certains des habitats les plus riches en biodiversité de la planète. Chaque scène d'écosystème présente de vrais faits scientifiques vérifiés par des chercheurs en environnement, soulignant notamment le rôle de l'abeille dont la pollinisation soutient 75 % des cultures alimentaires mondiales. Ce Doodle a été publié pendant les confinements mondiaux, offrant une fenêtre virtuelle sur la nature.`,
    controls: 'Cliquez sur les flèches ou glissez pour naviguer entre les différentes scènes d\'écosystèmes. Cliquez sur les animaux, plantes et éléments environnementaux dans chaque scène pour découvrir des faits fascinants sur cet écosystème. Répondez au quiz à la fin pour en apprendre plus sur votre empreinte écologique.',
  },
  'doctor-who-doodle': {
    title: 'Doodle Doctor Who',
    description:
      `Voyagez dans le temps avec le Docteur dans ce Google Doodle Doctor Who. Résolvez des énigmes et affrontez des ennemis emblématiques.\n\nPublié le 23 novembre 2013 pour célébrer le 50e anniversaire de Doctor Who, ce Doodle propose les onze incarnations du Docteur comme personnages jouables. Chaque période temporelle est peuplée de vilains emblématiques, dont les Daleks, les Cybermen, les Anges Pleureurs et les Zygons. Le jeu a été développé en étroite collaboration avec la BBC, en faisant l'un des rares Google Doodles officiellement sous licence d'une franchise télévisée.`,
    controls: 'Utilisez les touches fléchées pour déplacer le Docteur à travers chaque époque. Appuyez sur Z ou cliquez pour interagir avec les objets et personnages. Collectez les lettres Google volées en évitant les Daleks et autres ennemis. Chaque ère a différents dangers — restez vigilant et planifiez votre itinéraire soigneusement.',
  },
  'birth-of-hip-hop-doodle-game': {
    title: 'Naissance du Hip-Hop — Google Doodle',
    description:
      `Célébrez la naissance du hip-hop avec ce Google Doodle interactif. Mixez des vinyles et créez vos propres beats légendaires.\n\nPublié le 11 août 2017, ce Doodle commémore la légendaire fête de rentrée scolaire de DJ Kool Herc en 1973 au 1520 Sedgwick Avenue dans le Bronx, largement considérée comme le moment fondateur du hip-hop. Les platines interactives présentent des samples inspirés des artistes pionniers du genre, et la narration a été assurée par la légende du hip-hop Fab 5 Freddy. Ce fut l'un des Doodles les plus salués culturellement par la communauté hip-hop mondiale.`,
    controls: 'Cliquez sur le disque pour le scratcher d\'avant en arrière. Cliquez sur les pads de rythme pour superposer des samples de batterie sur la piste. Utilisez les curseurs du mixeur pour ajuster le volume de chaque élément. Cliquez sur différents vinyles pour changer de piste de base. Mélangez et combinez pour créer vos propres compositions hip-hop.',
  },
  'doodle-celebrating-loteria': {
    title: 'Doodle Lotería',
    description:
      `Jouez à la Lotería mexicaine dans ce Google Doodle multijoueur. Complétez votre grille et criez « Lotería ! » avant les autres.\n\nLa Lotería est une tradition mexicaine chérie depuis le XVIIIe siècle, initialement apportée d'Italie puis adaptée avec une imagerie et des vers rimés typiquement mexicains. Ce Doodle de 2019 a été le premier à proposer un mode multijoueur en temps réel contre des adversaires en ligne aléatoires, marquant un jalon dans l'histoire des Google Doodles. Les illustrations des cartes ont été créées par cinq artistes mexicains et mexico-américains, chacun apportant sa propre perspective culturelle.`,
    controls: 'Observez le crieur révéler les cartes une par une. Quand une carte révélée correspond à une case de votre plateau, cliquez dessus pour la marquer. Obtenez quatre cases marquées en ligne, colonne ou diagonale pour gagner et crier Lotería ! Restez attentif — les cartes défilent rapidement.',
  },
  'basketball-2012-google-doodle': {
    title: 'Basketball 2012 — Google Doodle',
    description:
      `Marquez des paniers dans ce Google Doodle Basketball des JO 2012. Visez juste et enchaînez les tirs pour battre le meilleur score.\n\nCe Doodle faisait partie d'une série de jeux sportifs interactifs publiés durant les Jeux olympiques d'été de Londres 2012, chacun célébrant une discipline athlétique différente. La mécanique de tir s'inspire de la physique réelle des lancers francs, avec un arc et une trajectoire calculés pour récompenser la constance technique. Les joueurs compétitifs ont découvert qu'un angle de lancement de 45 degrés à puissance moyenne offre les tirs les plus fiables.`,
    controls: 'Cliquez et faites glisser vers le haut sur le ballon pour viser votre tir, puis relâchez pour lancer. La direction et la longueur du glissement déterminent l\'angle et la puissance du tir. Un guide d\'arc apparaît pour vous aider à viser. Marquez avant la fin du temps pour gagner des étoiles.',
  },
  'celebrating-lake-xochimilco': {
    title: 'À la découverte du lac Xochimilco',
    description:
      `Explorez le lac Xochimilco et ses axolotls dans ce Google Doodle. Découvrez cet écosystème unique et protégez ses habitants.\n\nL'axolotl est une espèce en danger critique d'extinction que l'on ne trouve que dans les canaux de Xochimilco, avec moins de 1 000 individus restants à l'état sauvage. Ce Doodle met en lumière les chinampas, des îles flottantes artificielles créées par les Aztèques il y a plus de 500 ans, qui restent l'un des systèmes agricoles les plus durables jamais conçus. Les informations éducatives du jeu ont été développées avec l'aide de conservationnistes mexicains.`,
    controls: 'Utilisez les touches fléchées ou WASD pour nager avec l\'axolotl à travers les canaux. Ramassez les aliments flottant dans l\'eau pour marquer des points. Évitez les prédateurs et obstacles qui peuvent blesser l\'axolotl. Naviguez dans les canaux colorés de Xochimilco tout en découvrant cet écosystème unique.',
  },
  'celebrating-johann-sebastian-bach': {
    title: 'Hommage à Johann Sebastian Bach',
    description:
      `Composez de la musique dans le style de Bach avec l'intelligence artificielle de Google. Un Doodle musical fascinant et éducatif.\n\nPublié le 21 mars 2019 pour le 334e anniversaire de Bach, ce Doodle révolutionnaire utilise un modèle d'apprentissage automatique Coconet entraîné sur 306 harmonisations chorales de Bach. Ce fut le premier Google Doodle à utiliser l'intelligence artificielle comme mécanique centrale, permettant à chacun d'expérimenter les principes de la composition musicale baroque. Le modèle peut harmoniser n'importe quelle mélodie en moins de deux secondes, appliquant les mêmes règles de conduite des voix que Bach suivait il y a plus de 300 ans.`,
    controls: 'Cliquez sur la portée musicale pour placer des notes et créer une mélodie. Vous pouvez placer des notes sur n\'importe quelle ligne ou espace pour définir la hauteur, et cliquer sur les notes existantes pour les supprimer. Une fois votre mélodie créée, cliquez sur le bouton harmoniser pour que l\'IA la complète dans le style de Bach.',
  },
  'doodle-clara-rockmore': {
    title: 'Doodle de Clara Rockmore',
    description:
      `Jouez du thérémine avec Clara Rockmore dans ce Google Doodle interactif. Découvrez l'instrument le plus envoûtant de l'histoire de la musique.\n\nPublié le 9 mars 2016 pour ce qui aurait été le 105e anniversaire de Rockmore, ce Doodle simule fidèlement le thérémine — le seul instrument joué sans contact physique. Clara Rockmore était une enfant prodige violoniste de Lituanie qui s'est passionnée pour le thérémine après avoir rencontré son inventeur, Léon Theremin, dans les années 1920. Sa technique unique de « doigté aérien », qu'elle a développée pour atteindre une précision sans précédent, est encore étudiée par les théréministes aujourd'hui.`,
    controls: 'Déplacez la souris vers le haut et le bas pour changer la hauteur du thérémine — plus haut signifie un son plus aigu. Déplacez à gauche et à droite pour ajuster le volume. Cliquez et maintenez pour tenir une note. Sélectionnez l\'une des compositions de Clara Rockmore pour jouer en duo, ou créez librement votre propre morceau.',
  },
  'mothers-day-2013-doodle': {
    title: 'Doodle Fête des Mères 2013',
    description:
      `Créez un cadeau personnalisé pour votre maman dans ce Doodle Fête des Mères. Un jeu créatif et touchant de Google.\n\nGoogle célèbre la Fête des Mères avec des Doodles spéciaux depuis l'an 2000, passant de simples modifications du logo à des outils interactifs de création de cartes. L'édition 2013 a été l'une des premières à inclure une fonction de partage direct, permettant aux utilisateurs d'envoyer leurs créations par e-mail ou sur les réseaux sociaux en un seul clic. Le style d'illustration artisanal a été conçu pour évoquer la chaleur d'une carte faite main par un enfant.`,
    controls: 'Cliquez à travers les étapes de création de carte : choisissez d\'abord une scène illustrée, puis sélectionnez des éléments décoratifs comme des fleurs et des cœurs en cliquant dessus. Tapez votre message personnel dans le champ de texte. Utilisez les flèches pour parcourir plus d\'options. Quand vous avez terminé, partagez votre carte de Fête des Mères.',
  },
  'mothers-day-2020-doodle': {
    title: 'Doodle Fête des Mères 2020',
    description:
      `Envoyez une carte animée à votre mère avec ce Google Doodle Fête des Mères 2020. Personnalisez votre message d'amour.\n\nPublié pendant la pandémie de COVID-19 en mai 2020, ce Doodle a revêtu une résonance particulière alors que des millions de familles étaient confinées ensemble. Les vignettes animées illustrent des moments parentaux universels — des premiers pas au lâcher-prise — en utilisant des personnages animaliers pour transcender les frontières culturelles. Google a rapporté que les utilisateurs ont passé en moyenne trois minutes avec l'histoire interactive, bien plus longtemps que les temps d'engagement habituels des Doodles.`,
    controls: 'Cliquez ou appuyez pour avancer dans l\'histoire animée. Aux moments interactifs, suivez les indications à l\'écran — vous devrez peut-être cliquer sur des objets spécifiques, faire glisser des éléments ou appuyer en rythme avec l\'animation. L\'accent est mis sur le récit — profitez de l\'histoire émouvante et créez votre carte cadeau à la fin.',
  },
  'doodle-crossword-puzzle': {
    title: 'Doodle Mots Croisés',
    description:
      `Résolvez les mots croisés de Google dans ce Doodle interactif. Testez votre vocabulaire et votre culture générale.\n\nLes mots croisés ont été inventés par le journaliste Arthur Wynne et publiés pour la première fois dans le New York World le 21 décembre 1913. Ce Google Doodle célèbre cet anniversaire centenaire avec une grille entièrement interactive comprenant des indices astucieux faisant référence à l'histoire de Google et à la culture générale. Les amateurs de mots croisés apprécieront le style d'indices équitable et les moments de satisfaction cachés dans les jeux de mots.`,
    controls: 'Cliquez sur une case de la grille de mots croisés pour la sélectionner, puis tapez votre réponse au clavier. Cliquez sur l\'indice dans la liste pour mettre en surbrillance les cases correspondantes. Appuyez sur Tab pour passer à l\'indice suivant, ou cliquez sur un autre indice. Complétez tous les mots pour résoudre le puzzle.',
  },
  'slalom-canoe': {
    title: 'Slalom en Canoë',
    description:
      `Naviguez entre les portes du slalom en canoë dans ce Google Doodle sportif. Soyez rapide et précis pour décrocher la médaille d'or.\n\nLe slalom en canoë est un sport olympique depuis les Jeux de Munich en 1972, exigeant des athlètes de naviguer sur un parcours d'eaux vives de 250 mètres à travers 18 à 25 portes le plus rapidement possible. Ce Doodle capture le défi unique de lire les courants et de planifier sa trajectoire plusieurs portes à l'avance. La pénalité de deux secondes pour avoir touché un piquet de porte reflète les vraies règles olympiques, rendant chaque contact véritablement coûteux.`,
    controls: 'Maintenez la Flèche Gauche pour pagayer à gauche et la Flèche Droite pour pagayer à droite. Alternez entre les deux pour pagayer droit en aval. Passez votre canoë à travers chaque porte — les portes vertes doivent être passées en aval et les rouges en amont. Des pénalités de temps s\'ajoutent pour chaque porte manquée ou touchée.',
  },
  'eiji-tsuburayas-birthday': {
    title: 'Anniversaire d\'Eiji Tsuburaya',
    description:
      `Célébrez Eiji Tsuburaya, le père des effets spéciaux japonais, avec ce Google Doodle interactif inspiré des films de kaijū.\n\nPublié le 7 juillet 2015 pour ce qui aurait été le 114e anniversaire de Tsuburaya, ce Doodle rend hommage au genre « tokusatsu » du cinéma d'effets spéciaux en prises de vues réelles qu'il a créé. L'utilisation innovante par Tsuburaya de décors miniatures et d'effets pratiques dans le Godzilla original de 1954 a révolutionné le cinéma japonais et engendré un genre entier. Les mécaniques du jeu recréent astucieusement le timing précis nécessaire pour coordonner acteurs, pyrotechnie et mouvements de caméra sur un plateau miniature.`,
    controls: 'Cliquez sur le clap pour démarrer chaque scène. Puis cliquez sur les boutons à l\'écran dans le bon ordre pour déclencher les effets spéciaux au bon moment — lancez le monstre, déclenchez les explosions et dirigez l\'action. Le timing précis de vos clics détermine le spectacle de chaque scène.',
  },
  'doodle-roswells-66th-anniversary': {
    title: 'Doodle — 66e anniversaire de Roswell',
    description:
      `Aidez un extraterrestre échoué à Roswell dans ce Google Doodle point-and-click. Résolvez les énigmes et réparez la soucoupe volante.\n\nPublié le 8 juillet 2013 pour marquer le 66e anniversaire de l'incident de Roswell en 1947, ce Doodle est l'une des aventures point-and-click les plus élaborées de Google. Les trois scènes de puzzle interconnectées nécessitent des combinaisons d'objets créatives rappelant les jeux d'aventure classiques de LucasArts comme Monkey Island. Le style pixel art charmant et l'humour décalé lui ont valu un public fidèle de fans qui speedrunnent encore le puzzle aujourd'hui.`,
    controls: 'Cliquez sur les objets et personnages de la scène pour interagir. Ramassez des objets en cliquant dessus — ils vont dans votre inventaire. Combinez des objets de votre inventaire en cliquant sur l\'un puis sur l\'autre pour créer des outils. Utilisez ces outils dans l\'environnement pour résoudre des énigmes et aider l\'extraterrestre à rejoindre son vaisseau.',
  },
  'google-maps-snake': {
    title: 'Google Maps Snake — Jeu du Serpent',
    description:
      `Jouez au jeu du serpent sur Google Maps. Parcourez les villes du monde entier en récupérant des passagers dans ce jeu rétro revisité.\n\nPublié le 1er avril 2019 comme une surprise ludique du poisson d'avril, Google Maps Snake était accessible directement depuis l'application Google Maps sur iOS et Android. Chacune des six villes disponibles propose ses propres monuments réels comme objets à collecter — de la Tour Eiffel à Paris à l'Opéra de Sydney. Le jeu a été si populaire que Google l'a maintenu disponible bien après le poisson d'avril, et il reste jouable dans le navigateur aujourd'hui.`,
    controls: 'Utilisez les touches fléchées ou WASD pour diriger votre serpent dans les rues de la ville. Ramassez les icônes de monuments qui apparaissent sur la carte pour grandir et marquer des points. Évitez de heurter les bords de la carte ou votre propre queue. Choisissez parmi différentes villes du monde, chacune avec son propre plan et ses monuments.',
  },
  'celebrating-pani-puri': {
    title: 'Pani Puri à l\'honneur',
    description:
      `Découvrez le pani puri avec ce Google Doodle gourmand. Servez ce délicieux en-cas indien dans un mini-jeu interactif et coloré.\n\nPublié le 12 juillet 2023, ce Doodle célèbre le pani puri — connu sous différents noms en Asie du Sud, notamment golgappa, phuchka et pani ke patashe. Les clients de plus en plus exigeants du jeu reflètent l'expérience réelle de visiter un vendeur de rue, où rapidité et précision sont primordiales. Les origines du pani puri sont débattues entre plusieurs États indiens, mais son statut de l'un des en-cas de rue les plus appréciés au monde fait l'unanimité.`,
    controls: 'Cliquez ou appuyez sur le puri pour l\'ouvrir, puis appuyez rapidement sur les bons ingrédients de garniture selon la commande du client. Appuyez sur l\'eau épicée en dernier pour compléter le pani puri. Servez-le au client avant la fin du temps. Les commandes deviennent plus complexes au fil du jeu — gardez le rythme !',
  },
  'doodle-googles-15th-birthday': {
    title: 'Doodle — 15e anniversaire de Google',
    description:
      `Cassez la piñata pour fêter les 15 ans de Google dans ce Doodle festif. Un mini-jeu amusant célébrant l'anniversaire du moteur de recherche.\n\nPublié le 27 septembre 2013, ce Doodle a marqué le 15e anniversaire de Google depuis sa fondation par Larry Page et Sergey Brin dans une chambre de dortoir à Stanford en 1998. La mécanique de la piñata est un clin d'œil à l'ambiance festive de ce jalon, chaque cadeau tombant représentant un produit ou une fonctionnalité Google différent lancé au fil des années. La vitesse croissante des objets reflète la croissance rapide de Google, d'un simple moteur de recherche à un géant technologique mondial.`,
    controls: 'Déplacez la souris à gauche et à droite (ou utilisez les flèches) pour positionner la piñata sous les cadeaux qui tombent. Attrapez les boîtes cadeaux colorées pour marquer des points. Évitez les objets nuisibles — ils casseront la piñata. Ramassez le plus de cadeaux possible avant la fin du temps pour le meilleur score.',
  },
  'swing-dancing-and-the-savoy-ballroom': {
    title: 'Swing et le Savoy Ballroom !',
    description:
      `Dansez le swing au Savoy Ballroom dans ce Google Doodle animé. Revivez l'âge d'or du jazz et de la danse swing.\n\nLe Savoy Ballroom, ouvert de 1926 à 1958 à Harlem, New York, fut l'un des premiers lieux publics racialement intégrés aux États-Unis et le berceau du Lindy Hop. Ce Doodle a été créé avec la contribution de danseurs de swing professionnels et d'historiens pour garantir que les mouvements animés représentent fidèlement la chorégraphie authentique du Lindy Hop. La bande-son jazz entraînante s'inspire des compositions de Count Basie et Chick Webb, qui se produisaient régulièrement au Savoy original.`,
    controls: 'Appuyez sur les touches affichées à l\'écran en rythme avec la musique pour exécuter chaque mouvement de danse. Les flèches contrôlent les pas de base, tandis que les touches lettres déclenchent des mouvements spéciaux. Observez attentivement les indicateurs de rythme — le timing précis détermine votre score. Enchaînez des mouvements parfaits pour des combos.',
  },
  'doodle-qixi-festival-chilseok': {
    title: 'Doodle Festival Qixi / Chilseok',
    description:
      `Célébrez le festival Qixi avec ce Google Doodle romantique. Aidez les oiseaux à réunir les amoureux séparés par la Voie lactée.\n\nLa légende de Zhinü (la Tisserande, représentée par l'étoile Véga) et Niulang (le Bouvier, représenté par Altaïr) remonte à plus de 2 600 ans et est célébrée en Chine, au Japon, en Corée et au Vietnam. Selon le mythe, les amoureux furent bannis de chaque côté de la Voie lactée et ne peuvent se retrouver qu'une fois par an lorsque les pies forment un pont à travers la rivière céleste. Le style aquarelle de ce Doodle s'inspire des techniques de peinture au pinceau traditionnelles d'Asie de l'Est.`,
    controls: 'Cliquez ou appuyez pour guider la pie à travers le ciel nocturne. Ramassez des étoiles en volant pour construire le pont reliant les deux amoureux. Évitez les obstacles dans le ciel qui pourraient vous dévier. La pie se déplace dans la direction où vous cliquez — chronométrez vos touches pour naviguer entre les nuages.',
  },
  'chinese-new-year-snake-game': {
    title: 'Nouvel An Chinois — Jeu du Serpent',
    description:
      `Fêtez le Nouvel An chinois avec cette version spéciale du jeu du serpent. Collectez les lanternes rouges et célébrez l'année du Serpent.\n\nL'année du Serpent revêt une signification particulière dans l'astrologie chinoise, symbolisant la sagesse, l'élégance et l'intuition — faisant du jeu du serpent classique un choix thématique parfait. Les enveloppes rouges (hongbao) disséminées dans le jeu sont une tradition chérie du Nouvel An lunaire symbolisant la chance et la prospérité. La musique traditionnelle chinoise et la palette de couleurs vibrantes ont été soigneusement choisies pour capturer l'énergie festive de la plus grande célébration annuelle au monde.`,
    controls: 'Utilisez les touches fléchées ou WASD pour diriger le dragon-serpent sur le plateau festif. Ramassez les biscuits de fortune et les enveloppes rouges pour grandir et marquer des points. Évitez de heurter les murs ou votre propre corps. Le serpent avance en continu et accélère à mesure que vous ramassez plus d\'objets.',
  },
  'doodle-celebrating-mbira': {
    title: 'Doodle Mbira à l\'honneur',
    description:
      `Jouez du mbira dans ce Google Doodle musical. Découvrez cet instrument africain traditionnel et créez vos propres mélodies.\n\nLe mbira dzavadzimu est au cœur des cérémonies spirituelles shona depuis plus de 1 000 ans et a été reconnu par l'UNESCO comme Patrimoine Culturel Immatériel en 2020. Ce Doodle a été développé en collaboration avec des maîtres du mbira zimbabwéens pour garantir l'authenticité des mélodies et des rythmes traditionnels shona. Les motifs hypnotiques imbriqués de l'instrument, produits en pinçant 22 à 28 lames métalliques montées sur une caisse de résonance en bois, créent un son chatoyant et envoûtant unique au monde.`,
    controls: 'Observez les boules colorées descendre vers les lames métalliques de la mbira en bas de l\'écran. Appuyez sur la touche correspondante (ou touchez la lame sur mobile) quand la boule atteint la lame. Frappez les notes en rythme pour maintenir la mélodie. Les chansons deviennent plus rapides et complexes au fil du jeu.',
  },
  'hurdles-2012': {
    title: 'Course de Haies 2012',
    description:
      `Courez et sautez les haies dans ce Google Doodle des JO 2012. Chronométrez vos sauts pour décrocher la médaille d'or.\n\nCe Doodle faisait partie d'une série de mini-jeux interactifs créés par Google pour les Jeux olympiques de Londres 2012. Il est devenu l'un des Doodles olympiques les plus populaires de tous les temps, avec des joueurs du monde entier rivalisant pour établir les temps les plus rapides. La mécanique de martèlement rapide des touches en a fait un passe-temps favori au bureau pendant les Jeux.`,
    controls: 'Alternez rapidement entre les flèches gauche et droite pour faire courir votre athlète plus vite. Appuyez sur Flèche Haut (ou Espace) pour sauter chaque haie. Le timing est essentiel — sauter trop tôt ou trop tard vous ralentira. Maintenez un rythme régulier pour atteindre la vitesse maximale entre les haies.',
  },
  'soccer-2012': {
    title: 'Football 2012',
    description:
      `Arrêtez les tirs au but dans ce Google Doodle Football des JO 2012. Plongez au bon moment pour sauver votre équipe.\n\nFaisant partie de la célèbre série de Doodles des Jeux olympiques de Londres 2012, ce jeu de tirs au but capture la tension du moment le plus dramatique du football mondial. Les tireurs de l'IA deviennent progressivement plus difficiles à lire au fil des manches, avec des frappes plus rapides et des courses d'élan plus trompeuses. Il reste l'un des Doodles sportifs les plus rejoués de l'histoire de Google.`,
    controls: 'Appuyez sur Flèche Gauche pour plonger à gauche, Flèche Droite pour plonger à droite, ou Flèche Haut pour rester au centre et sauter. Observez la course d\'élan et la position du tireur pour anticiper la direction du ballon. Réagissez rapidement pour arrêter le plus de tirs possible.',
  },
  'global-candy-cup-2015': {
    title: 'Coupe Mondiale des Bonbons Halloween 2015',
    description:
      `Récoltez des bonbons pour votre équipe dans la Coupe Mondiale Halloween 2015. Un Google Doodle multijoueur festif et compétitif.\n\nCe Doodle occupe une place spéciale dans l'histoire de Google en tant que première apparition de Momo, le chat noir qui allait devenir la star de la série très populaire Magic Cat Academy. Le jeu proposait un classement mondial en temps réel qui suivait les totaux de bonbons par équipe à travers le monde. Ce fut le premier Doodle Halloween compétitif multijoueur de Google.`,
    controls: 'Cliquez ou appuyez pour faire voler votre sorcière et ramasser des bonbons. Dirigez-vous à travers le parcours hanté en cliquant plus haut ou plus bas sur l\'écran pour ajuster l\'altitude. Ramassez le plus de bonbons possible en évitant les obstacles fantomatiques. Rivalisez pour le meilleur score de votre équipe.',
  },
  'magic-cat-academy-2': {
    title: 'Académie des Chats Magiques 2',
    description:
      `Momo le chat revient pour une aventure sous-marine. Dessinez des symboles pour vaincre les fantômes des profondeurs dans cette suite magique.\n\nPublié pour Halloween 2020 pendant la pandémie, cette suite a emmené Momo sous les vagues pour une aventure entièrement aquatique. L'équipe de développement de Google s'est inspirée des créatures des profondeurs marines pour le design des fantômes, créant certains des ennemis les plus mémorables de la série. Ce fut le premier Doodle Halloween à s'étendre sur plusieurs biomes sous-marins distincts.`,
    controls: 'Dessinez le symbole affiché au-dessus de chaque fantôme en cliquant et glissant (ou en glissant sur mobile). Tracez le symbole avec précision avant que le fantôme n\'atteigne Momo. Plusieurs fantômes peuvent apparaître simultanément — priorisez les plus proches. Les nouveaux types de fantômes introduisent des symboles plus complexes au fil du jeu.',
  },
  'magic-cat-academy-3': {
    title: 'Académie des Chats Magiques 3',
    description:
      `Retrouvez Momo dans le troisième volet de Magic Cat Academy. De nouveaux défis et de nouveaux ennemis vous attendent dans cette aventure féline.\n\nPublié pour Halloween 2024, cet épisode représente le jeu Magic Cat Academy le plus ambitieux à ce jour, proposant cinq mondes entièrement réalisés avec des thèmes visuels uniques. L'équipe Google Doodle a consacré plus d'un an au développement du système de sorts combo étendu et des combats de boss en plusieurs phases. Les fans le considèrent comme l'opus définitif de cette trilogie bien-aimée.`,
    controls: 'Dessinez le symbole au-dessus de chaque fantôme en cliquant et glissant (ou en glissant sur mobile). Nouveauté dans cette édition : enchaînez plusieurs symboles pour des sorts combo puissants. Certains fantômes nécessitent des séquences de symboles spécifiques pour être vaincus. Soyez attentif aux nouveaux types d\'ennemis et adaptez votre stratégie.',
  },
  'rubiks-cube': {
    title: 'Rubik\'s Cube',
    description:
      `Résolvez le Rubik's Cube dans ce Google Doodle interactif. Faites tourner les faces et alignez les couleurs dans ce casse-tête mythique.\n\nCe Doodle a été créé pour célébrer le 40e anniversaire de l'invention d'Ernő Rubik, dont plus de 450 millions d'exemplaires ont été vendus dans le monde depuis ses débuts en 1974. La version de Google reproduit fidèlement les mécaniques de rotation 3D, en faisant l'un des Doodles interactifs les plus impressionnants sur le plan technique. Des speedcubers ont rapporté l'utiliser pour pratiquer leurs algorithmes loin d'un cube physique.`,
    controls: 'Cliquez et faites glisser sur une rangée ou colonne du cube pour faire tourner cette couche. Glissez sur le fond pour faire pivoter le cube entier et voir les différentes faces. Cliquez sur le bouton Mélanger pour désorganiser le cube. Pour le réinitialiser, cliquez sur Réinitialiser. Essayez de résoudre les six faces avec le moins de mouvements possible.',
  },
  'oskar-fischinger': {
    title: 'Oskar Fischinger — Jouet Musical',
    description:
      `Créez de la musique visuelle inspirée par Oskar Fischinger. Composez des mélodies en plaçant des formes géométriques dans ce Doodle artistique.\n\nFischinger était un animateur abstrait germano-américain dont l'œuvre pionnière des années 1930 à 1960 a exploré la relation entre l'art visuel et la musique. Ses films ont inspiré des créations allant de Fantasia de Disney aux visualiseurs musicaux modernes. Ce Doodle vous permet d'expérimenter sa philosophie créative de première main, transformant de simples clics en une œuvre audiovisuelle vivante.`,
    controls: 'Cliquez sur n\'importe quelle cellule de la grille pour placer une note — chaque colonne représente un temps de la boucle, chaque rangée une hauteur différente. Cliquez sur une cellule pleine pour la supprimer. Utilisez le sélecteur d\'instruments pour changer le son. Ajustez le tempo et la gamme avec les contrôles. La composition tourne en boucle pendant que vous créez.',
  },
  'celebrating-popcorn': {
    title: 'Le Popcorn à l\'honneur',
    description:
      `Célébrez le popcorn avec ce Google Doodle interactif. Découvrez l'histoire de cette gourmandise dans un mini-jeu croustillant.\n\nCe Doodle de 2024 s'inspire de la riche histoire du popcorn, dégusté depuis des milliers d'années — des preuves archéologiques suggèrent que l'on faisait éclater du maïs dès 3600 av. J.-C. Le mode multijoueur en a fait l'un des Google Doodles les plus sociaux jamais publiés, avec des joueurs s'affrontant en temps réel. Son style artistique coloré et son gameplay frénétique en ont fait un succès viral sur les réseaux sociaux.`,
    controls: 'Cliquez ou appuyez rapidement pour faire éclater les grains de maïs et remplir votre seau. Ramassez les bonus qui apparaissent à l\'écran pour des avantages temporaires comme un éclatement plus rapide ou des boucliers. Évitez le popcorn volant des autres joueurs. Remplissez votre seau avant vos adversaires pour gagner chaque manche.',
  },
  'wewa-weaving': {
    title: 'Tissage We:wa',
    description:
      `Découvrez l'art du tissage traditionnel We:wa dans ce Google Doodle. Créez des motifs colorés en tissant virtuellement.\n\nWe:wa (1849–1896) était un remarquable lhamana zuni (un rôle traditionnel de troisième genre) devenu l'un des tisserands et potiers les plus accomplis du pueblo zuni. Ce Doodle a été créé en collaboration avec des conseillers culturels zunis pour garantir une représentation authentique des motifs et techniques de tissage. Il sert à la fois de jeu et d'hommage à l'art textile durable des traditions autochtones.`,
    controls: 'Cliquez et faites glisser les fils de laine horizontalement à travers le métier pour tisser chaque rangée. Sélectionnez différentes couleurs de laine dans la palette sur le côté. Suivez le motif affiché en haut pour reproduire un design de tissage traditionnel. Faites attention au placement des couleurs — la précision détermine votre score final.',
  },
  'rise-of-the-half-moon': {
    title: 'L\'Ascension du Croissant de Lune',
    description:
      `Guidez la lune dans son ascension dans ce Google Doodle poétique. Un jeu enchanteur mêlant musique et narration visuelle.\n\nCe Doodle récurrent innovant se met à jour chaque mois avec du nouveau contenu lié aux cycles lunaires et aux célébrations culturelles du monde entier. La mécanique du jeu de cartes a été conçue par l'équipe Google Doodle pour rendre l'astronomie et la mythologie mondiale accessibles et captivantes. Chaque édition mensuelle propose de nouvelles cartes illustrées avec des faits provenant d'astronomes et d'historiens des cultures.`,
    controls: 'À votre tour, sélectionnez une carte de votre main qui se connecte à la carte actuelle sur la table — les cartes doivent partager une phase lunaire, une couleur ou une catégorie. Gagnez des points bonus en créant des chaînes de cartes correspondantes. Utilisez les cartes spéciales stratégiquement pour maximiser votre score et surpasser votre adversaire.',
  },
  zamboni: {
    title: 'Anniversaire de Frank Zamboni',
    description:
      `Conduisez une surfaceuse Zamboni dans ce Google Doodle. Nettoyez la patinoire avant que le temps ne s'écoule dans ce mini-jeu glacé.\n\nFrank Zamboni a inventé la première surfaceuse de glace en 1949 à Paramount, en Californie, révolutionnant l'entretien des patinoires à jamais. Avant son invention, resurfacer une patinoire nécessitait une équipe d'ouvriers et prenait plus d'une heure — la Zamboni l'a réduit à quelques minutes. La mécanique de nettoyage satisfaisante de ce Doodle capture parfaitement pourquoi regarder une Zamboni travailler est devenu un passe-temps apprécié des spectateurs de hockey.`,
    controls: 'Utilisez les touches fléchées pour diriger la Zamboni sur la patinoire. Roulez sur chaque zone de glace sale pour la nettoyer — les zones nettoyées deviennent blanc brillant. Planifiez votre trajet soigneusement pour couvrir toute la patinoire sans repasser au même endroit. Nettoyez toute la glace avant la fin du temps.',
  },
  'komodo-national-park': {
    title: 'Parc National de Komodo',
    description:
      `Explorez le parc national de Komodo dans ce Google Doodle. Découvrez les dragons de Komodo et la faune fascinante de l'île.\n\nLe parc national de Komodo, créé en 1980 en Indonésie, abrite environ 3 000 dragons de Komodo — les plus grands lézards vivants de la Terre, pouvant atteindre 3 mètres de long. Les illustrations somptueuses du Doodle ont été peintes à la main par l'équipe Google Doodle pour capturer la beauté luxuriante de l'archipel indonésien. Il reste l'un des Doodles éducatifs les mieux notés grâce à son mélange parfait de faits amusants et d'illustrations magnifiques.`,
    controls: 'Lisez attentivement chaque question sur les dragons de Komodo, puis cliquez sur la réponse que vous pensez correcte parmi les options. Vous recevrez un retour immédiat sur votre réponse. Découvrez des faits fascinants sur les dragons de Komodo et leur habitat en progressant dans le quiz.',
  },
  'les-paul-guitar': {
    title: 'Doodle Guitare Les Paul',
    description:
      `Jouez de la guitare virtuelle dans ce Google Doodle rendant hommage à Les Paul. Grattez les cordes et enregistrez vos mélodies.\n\nLes Paul (1915–2009) n'était pas seulement un guitariste légendaire mais aussi un pionnier de l'enregistrement multipiste et de la guitare électrique à corps plein qui porte son nom. Ce Doodle de 2011 est devenu si populaire que Google l'a maintenu en ligne 48 heures supplémentaires et lui a ensuite donné une page permanente. Les utilisateurs ont collectivement passé plus de 40 millions d'heures à y jouer durant les 48 premières heures.`,
    controls: 'Survolez les cordes de la guitare avec la souris pour les gratter, ou cliquez sur des cordes individuelles pour pincer des notes. Vous pouvez aussi utiliser le clavier — chaque touche joue une note différente. Appuyez sur le bouton enregistrer pour capturer votre performance et la réécouter. Expérimentez avec différentes mélodies et accords.',
  },
  'robert-moog-synthesizer': {
    title: 'Doodle Synthétiseur Robert Moog',
    description:
      `Jouez du synthétiseur Moog dans ce Google Doodle musical. Tournez les boutons et créez des sons électroniques uniques.\n\nRobert Moog (1934–2005) a révolutionné la musique en créant le premier synthétiseur commercialisé, qui a façonné le son d'artistes allant de Wendy Carlos à Kraftwerk. Ce Doodle reproduit fidèlement l'interface du Minimoog Model D, y compris ses emblématiques molettes de pitch et de modulation. L'enregistreur 4 pistes vous permet d'expérimenter la technique de superposition multipiste que les instruments de Moog ont rendue possible.`,
    controls: 'Cliquez sur les touches du clavier en bas pour jouer des notes. Ajustez les boutons et interrupteurs au-dessus pour modifier les formes d\'onde de l\'oscillateur, la fréquence de coupure du filtre, la résonance et les réglages d\'enveloppe. Utilisez l\'enregistreur 4 pistes à droite pour superposer plusieurs sons. Expérimentez les réglages pour créer des sons uniques.',
  },
  'alan-turing-machine': {
    title: 'Doodle Machine d\'Alan Turing',
    description:
      `Résolvez des puzzles logiques inspirés de la machine de Turing dans ce Google Doodle. Un hommage au père de l'informatique moderne.\n\nAlan Turing (1912–1954) est largement considéré comme le père de l'informatique théorique et de l'intelligence artificielle. Son concept de machine de Turing — un modèle mathématique de calcul — a posé les fondations de chaque ordinateur moderne. Ce Doodle distille brillamment une théorie computationnelle complexe en puzzles accessibles, rendant le génie de Turing tangible pour quiconque souhaite essayer.`,
    controls: 'Chaque puzzle affiche une séquence binaire cible en haut. La machine de Turing lit la bande et suit vos règles programmées. Cliquez sur les cellules du tableau d\'états pour alterner entre les actions (écrire 0, écrire 1, déplacer à gauche, déplacer à droite, changer d\'état). Programmez les bonnes règles pour que la machine produise la séquence cible.',
  },
  'pangolin-love': {
    title: 'Pangolin Love — Saint-Valentin 2017',
    description:
      `Aidez un pangolin à trouver l'amour dans ce Google Doodle de la Saint-Valentin 2017. Un jeu de plateforme mignon et romantique.\n\nLes pangolins sont les mammifères les plus braconnés au monde, ce qui rend ce Doodle de la Saint-Valentin à la fois charmant et porteur de sens. Les quatre niveaux se déroulent au Ghana, en Inde, en Chine et aux Philippines — représentant les habitats naturels de différentes espèces de pangolins. Google s'est associé à des organisations de conservation de la faune pour sensibiliser le public, et le Doodle a contribué à attirer l'attention mondiale sur la protection des pangolins.`,
    controls: 'Cliquez ou appuyez pour faire sauter le pangolin et interagir avec l\'environnement. Ramassez des objets romantiques comme du chocolat, des fleurs et des cœurs dispersés dans chaque niveau. Évitez les obstacles et dangers sur votre chemin. Complétez chaque niveau pour faire avancer l\'histoire d\'amour du pangolin.',
  },
  'santa-tracker-elf-maker': {
    title: 'Santa Tracker — Créateur d\'Elfes',
    description:
      `Créez votre propre elfe du Père Noël dans cet outil créatif de Google Santa Tracker. Personnalisez chaque détail pour un elfe unique.\n\nLe Santa Tracker de Google est une tradition festive appréciée depuis 2004, et le Créateur d'Elfes est l'une de ses activités les plus populaires au fil des ans. Le créateur de personnages offre des milliers de combinaisons possibles, garantissant que deux elfes ne se ressemblent jamais. Les elfes créés peuvent être sauvegardés comme avatars de profil ou partagés comme cartes de vœux de Noël avec ses proches.`,
    controls: 'Cliquez sur les catégories de personnalisation en bas — tête, corps, accessoires et couleurs. Cliquez sur les éléments individuels pour les appliquer à votre lutin. Utilisez les boutons fléchés pour parcourir plus d\'options dans chaque catégorie. Quand vous êtes satisfait de votre lutin personnalisé, enregistrez-le ou partagez-le avec vos amis.',
  },
  'santa-tracker-snowball-storm': {
    title: 'Santa Tracker — Tempête de Boules de Neige',
    description:
      `Lancez des boules de neige dans cette bataille hivernale de Google Santa Tracker. Visez bien et remportez la guerre des neiges.\n\nCe mini-jeu riche en action est l'une des entrées les plus compétitives de la collection Santa Tracker de Google. Le moteur physique crée des arcs et des impacts de boules de neige réalistes, rendant chaque lancer satisfaisant. Les bonus comme la boule de neige géante et le mode tir rapide ajoutent de la profondeur stratégique à ce qui commence comme un simple jeu de lancer.`,
    controls: 'Déplacez la souris pour viser votre lancer de boules de neige. Cliquez pour lancer une boule de neige sur vos adversaires. Cachez-vous derrière votre fort de neige en descendant pour éviter les boules de neige entrantes. Ramassez les bonus qui apparaissent pour des avantages temporaires comme le tir rapide ou les boules de neige géantes.',
  },
  'santa-tracker-present-drop': {
    title: 'Santa Tracker — Distribution de Cadeaux',
    description:
      `Aidez le Père Noël à livrer les cadeaux dans ce mini-jeu de Google Santa Tracker. Larguez les paquets dans les cheminées.\n\nPresent Drop est une version moderne des jeux d'arcade classiques de bombardement, réimaginée avec un charme festif de Noël. La courbe de difficulté est soigneusement calibrée : les premiers quartiers défilent lentement pour vous laisser le temps d'apprendre le timing avant que le rythme ne s'accélère considérablement. Les meilleurs scores exigent de mémoriser les positions des cheminées et de développer un rythme pour vos largages.`,
    controls: 'Le traîneau du Père Noël vole automatiquement à travers l\'écran. Cliquez ou appuyez sur Espace pour lâcher un cadeau. Chronométrez vos largages pour que les cadeaux tombent directement dans les cheminées en dessous. Chaque livraison réussie rapporte des points — plus le tir est précis, plus le score est élevé. Ne gaspillez pas de cadeaux en ratant les cheminées.',
  },
  'santa-tracker-penguin-dash': {
    title: 'Santa Tracker — Course de Pingouins',
    description:
      `Faites glisser votre pingouin sur la glace dans cette course folle de Google Santa Tracker. Collectez les poissons et évitez les obstacles.\n\nCe runner infini s'inspire des jeux de défilement latéral classiques tout en ajoutant une physique de glace glissante qui donne au mouvement un caractère typiquement pingouin. Les parcours générés de manière procédurale garantissent que deux courses ne sont jamais identiques, gardant le gameplay frais à chaque tentative. C'est l'un des jeux les plus adaptés aux familles de la collection Santa Tracker, avec des commandes simples que les enfants de tous âges peuvent maîtriser.`,
    controls: 'Utilisez les touches fléchées ou touchez l\'écran pour contrôler votre pingouin. Appuyez sur Haut ou Espace pour sauter les obstacles. Appuyez sur Bas pour glisser sous les barrières. Ramassez les cannes de sucre et friandises de Noël pour des points bonus. Évitez les obstacles — chaque choc vous ralentit. Allez le plus loin possible.',
  },
  'santa-tracker-code-lab': {
    title: 'Santa Tracker — Labo de Code',
    description:
      `Apprenez à coder avec le Labo de Code de Google Santa Tracker. Programmez les déplacements d'un elfe de manière ludique et éducative.\n\nConstruit sur le framework de programmation visuelle Blockly de Google, le Labo de Code introduit les concepts fondamentaux de la programmation — séquences, boucles et conditions — dans un cadre festif accessible. La difficulté progressive introduit de nouveaux blocs graduellement, renforçant la confiance avant d'ajouter de la complexité. Il est utilisé par des éducateurs du monde entier comme une introduction ludique à la pensée computationnelle pendant les fêtes.`,
    controls: 'Faites glisser les blocs de code de la boîte à outils vers l\'espace de travail. Emboîtez les blocs pour créer une séquence d\'instructions. Utilisez les blocs de mouvement pour guider le lutin, les blocs de boucle pour répéter des actions et les blocs conditionnels pour prendre des décisions. Appuyez sur Exécuter pour tester votre programme. Ajustez votre code si le lutin n\'atteint pas l\'objectif.',
  },
  'chrome-music-lab-rhythm': {
    title: 'Chrome Music Lab — Jeu de Rythme',
    description:
      `Explorez le rythme avec Chrome Music Lab. Créez des séquences rythmiques et découvrez les bases de la musique de manière interactive.\n\nChrome Music Lab a été créé par le Creative Lab de Google en collaboration avec des musiciens et des développeurs pour rendre l'apprentissage musical plus accessible. L'expérience Rythme aide spécifiquement les utilisateurs à comprendre les signatures temporelles, les polyrythmies et l'interaction entre différents motifs de percussion. Il est utilisé dans les salles de cours de musique du monde entier comme outil pédagogique interactif pour les concepts rythmiques.`,
    controls: 'Cliquez sur les cellules de la grille pour placer des rythmes. Chaque rangée représente un son de percussion différent. Cliquez sur une cellule pour activer ou désactiver un temps. La tête de lecture se déplace continuellement à travers la grille, jouant les rythmes rencontrés. Expérimentez différents motifs pour créer vos propres rythmes.',
  },
  'chrome-music-lab-song-maker': {
    title: 'Chrome Music Lab — Créateur de Chansons',
    description:
      `Composez vos propres chansons avec le Song Maker de Chrome Music Lab. Un outil musical intuitif et coloré pour tous les âges.\n\nSong Maker est devenu l'expérience Chrome Music Lab la plus populaire, utilisée par des millions d'enseignants et d'élèves dans le monde entier pour l'éducation musicale. Sa grille aux codes couleurs correspond directement aux gammes musicales, rendant la théorie musicale visuelle et intuitive même pour les débutants complets. Les chansons créées peuvent être sauvegardées et partagées via des URL uniques, donnant naissance à une communauté en ligne dynamique de compositions partagées.`,
    controls: 'Cliquez sur la grille du haut pour placer des notes mélodiques — chaque colonne est un temps, chaque rangée est une hauteur. Cliquez sur la grille du bas pour ajouter des rythmes de batterie. Appuyez sur le bouton lecture pour écouter votre composition. Utilisez le sélecteur d\'instruments pour changer les sons. Enregistrez et partagez vos créations musicales.',
  },
  'chrome-music-lab-kandinsky': {
    title: 'Chrome Music Lab — Kandinsky de Google',
    description:
      `Transformez vos dessins en musique, inspiré par Kandinsky. Dessinez des formes et écoutez-les prendre vie dans Chrome Music Lab.\n\nWassily Kandinsky (1866–1944) était un peintre et théoricien de l'art russe qui croyait profondément au lien entre l'art visuel et la musique, un concept appelé synesthésie. Cette expérience donne vie à sa philosophie en associant formes, couleurs et positions à des éléments musicaux. C'est une magnifique démonstration de la façon dont la créativité visuelle et auditive peut fusionner en un seul moyen d'expression.`,
    controls: 'Dessinez sur le canevas avec la souris ou le doigt. Chaque trait crée un son — les lignes produisent des tons soutenus, les cercles créent des frappes percussives et les triangles ajoutent des accents aigus. Différentes couleurs correspondent à différents instruments. Appuyez sur lecture pour écouter votre œuvre d\'art comme une composition musicale.',
  },
  'quick-draw': {
    title: 'Quick, Draw! de Google',
    description: `Un réseau de neurones peut-il apprendre à reconnaître vos gribouillages ? Quick, Draw! est une expérience d'IA de Google qui vous défie de dessiner des objets du quotidien en moins de 20 secondes pendant qu'un modèle d'apprentissage automatique tente de deviner ce que vous dessinez en temps réel.

Quick, Draw! a été créé comme une expérience d'IA pour aider à entraîner le réseau de neurones de Google à la reconnaissance d'images dessinées à la main. Chaque gribouillage soumis par les joueurs fait partie d'un immense ensemble de données open source utilisé par des chercheurs du monde entier.`,
    controls: `On vous donne un mot et 20 secondes pour le dessiner avec la souris ou le doigt. Commencez à dessiner dès que le minuteur démarre — l'IA analyse vos traits en temps réel et crie ses devinettes. Si elle devine correctement, vous passez au mot suivant. Six tours au total.`,
  },
  'google-earth-flight-simulator': {
    title: 'Earth — Carte des Vents et Météo',
    description: `Explorez la planète entière sur un magnifique globe 3D animé qui visualise les courants de vent en temps réel, les conditions météorologiques et les courants océaniques. Faites tourner le globe, zoomez sur n'importe quelle région et observez les flux d'air animés fascinants.

Alimenté par des prévisions de supercalculateurs mises à jour toutes les trois heures, ce globe interactif utilise des données atmosphériques du Global Forecast System de NOAA et des données océaniques d'OSCAR. Créé par Cameron Beccario, le projet transforme des téraoctets de données scientifiques en une animation captivante en temps réel.`,
    controls: `Cliquez et faites glisser pour faire tourner le globe. Défilez pour zoomer. Cliquez n'importe où pour voir la vitesse du vent, la température et les coordonnées locales. Utilisez le menu (texte "earth" en bas à gauche) pour changer de couche atmosphérique.`,
  },
};
