import type { GameTranslation } from '../gameTranslations';

export const HI_GAMES: Record<string, GameTranslation> = {
  'baseball': {
    title: 'Google Doodle बेसबॉल',
    description: `इस मज़ेदार Google Doodle बेसबॉल गेम में फ़ास्ट फ़ूड के खिलाफ़ बैटिंग करें। सही समय पर स्विंग करके रन बनाएँ।\n\nयह डूडल मूल रूप से 4 जुलाई, 2019 को अमेरिका के स्वतंत्रता दिवस और ग्रीष्मकालीन कुकआउट के उपलक्ष्य में जारी किया गया था। जापानी एनिमेशन स्टूडियो STUDIO4°C के सहयोग से बनाया गया यह डूडल Google के इतिहास में सबसे अधिक बार खेले जाने वाले खेल डूडल में से एक है।`,
    controls: 'बैट स्विंग करने के लिए क्लिक करें या टैप करें। पिचर की विंडअप देखें और सही समय पर क्लिक करें ताकि बॉल से संपर्क हो। होम रन मारने के लिए स्वीट स्पॉट पर निशाना लगाएं। आपको प्रति इनिंग तीन आउट मिलते हैं — जितने अधिक रन हो सके स्कोर करने के लिए अपने स्विंग का सही समय चुनें।',
  },
  'doodle-cricket-game': {
    title: 'Doodle क्रिकेट गेम',
    description: `इस मज़ेदार Google Doodle में एक क्रिकेट बनकर क्रिकेट खेलें। बैटिंग करें और ज़्यादा से ज़्यादा रन बनाएँ।\n\nयह डूडल 18 जून, 2017 को ICC चैंपियंस ट्रॉफी के अवसर पर जारी किया गया था। इसमें घोंघे, तितलियाँ और लेडीबग जैसे प्यारे कीट प्रतिद्वंद्वी हैं जो क्रिकेट को वैश्विक दर्शकों के लिए सुलभ बनाते हैं। यह Google के सबसे वायरल इंटरैक्टिव डूडल में से एक बन गया।`,
    controls: 'जब गेंद फेंकी जाए तो बैट स्विंग करने के लिए क्लिक करें या टैप करें। टाइमिंग ही सब कुछ है — बहुत जल्दी या बहुत देर से स्विंग करेंगे तो मिस हो जाएगा। गेंद को हिट करके रन बनाएं। जब आपके सभी विकेट गिर जाएं तो खेल समाप्त हो जाता है। आउट होने से पहले जितने अधिक रन हो सके बनाने की कोशिश करें।',
  },
  'snake': {
    title: 'Google Snake गेम',
    description: `Google का क्लासिक स्नेक गेम खेलें। सेब खाएँ, बड़े होते जाएँ और खुद से टकराने से बचें।\n\nस्नेक गेम का इतिहास 1970 के दशक के अंत से है, लेकिन 1998 में Nokia ने इसे अपने मोबाइल फ़ोन में प्री-इंस्टॉल करके विश्वव्यापी लोकप्रियता दिलाई। Google का ब्राउज़र संस्करण उसी नशीली सादगी को रंगीन ग्राफ़िक्स और स्मूद एनिमेशन के साथ प्रस्तुत करता है।`,
    controls: 'अपने सांप को चलाने के लिए एरो कीज़ या WASD का उपयोग करें। बोर्ड पर दिखने वाले फल खाकर लंबे होते जाएं और अंक प्राप्त करें। दीवारों या अपनी पूंछ से टकराने से बचें — दोनों से खेल समाप्त हो जाता है। सांप लगातार चलता रहता है, इसलिए अपना रास्ता पहले से तय करें। मोबाइल पर, जिस दिशा में मुड़ना हो उस दिशा में स्वाइप करें।',
  },
  'pacman': {
    title: 'Google Pac-Man गेम',
    description: `इस Google Doodle में क्लासिक आर्केड Pac-Man को फिर से जिएँ। डॉट्स खाएँ और भूतों से बचें।\n\n21 मई, 2010 को जारी यह Google का पहला प्लेयेबल होमपेज डूडल था, जिसने कथित तौर पर लॉन्च के दिन दुनिया को $120 मिलियन की उत्पादकता हानि पहुंचाई। 'Insert Coin' पर दो बार क्लिक करके छिपा हुआ दो-खिलाड़ी मोड सक्रिय होता है जिसमें Ms. Pac-Man WASD कीज़ से नियंत्रित होती है।`,
    controls: 'पैक-मैन को भूलभुलैया में चलाने के लिए एरो कीज़ का उपयोग करें। लेवल पूरा करने के लिए सभी छोटे डॉट्स खाएं। चार भूतों से बचें — किसी एक को छूने से एक जान जाती है। बड़ी पावर पेलेट खाकर भूतों को अस्थायी रूप से नीला बनाएं, जिससे आप उन्हें बोनस अंकों के लिए खा सकें। दो खिलाड़ियों के मोड में, खिलाड़ी 2 W/A/S/D का उपयोग करता है।',
  },
  'champion-island-games': {
    title: 'Google Doodle चैंपियन आइलैंड गेम्स',
    description: `इस शानदार Google Doodle RPG में चैंपियन आइलैंड की खोज करें। सात खेल मिनी-गेम और गुप्त मिशन पूरे करें।\n\n23 जुलाई, 2021 को टोक्यो ओलंपिक के उद्घाटन समारोह के साथ जारी, यह अब तक के सबसे महत्वाकांक्षी Google डूडल में से एक है। जापानी एनिमेशन स्टूडियो STUDIO4°C के साथ विकसित, इसमें जापानी पौराणिक कथाओं से प्रेरित एक समृद्ध दुनिया है और सात घंटे से अधिक की सामग्री है।`,
    controls: 'लकी को द्वीप पर चलाने के लिए एरो कीज़ का उपयोग करें। किरदारों से बात करने और मिनी-गेम में प्रवेश करने के लिए Z दबाएं या क्लिक करें। हर खेल के अपने नियंत्रण स्क्रीन पर दिखाए जाते हैं — उदाहरण के लिए, मैराथन दौड़ के लिए स्पेसबार को लयबद्ध रूप से दबाएं, या तीरंदाजी के लिए क्लिक करके निशाना लगाएं और छोड़ें। सभी सात चैंपियंस को खोजने के लिए पूरे द्वीप की खोज करें।',
  },
  'magic-cat-academy': {
    title: 'मैजिक कैट अकादमी (Magic Cat Academy)',
    description: `बिल्ली Momo की मदद करें और जादुई चिन्ह बनाकर भूतों से अपने स्कूल की रक्षा करें।\n\n30 अक्टूबर, 2016 को जारी इस डूडल में मोमो का डिज़ाइन एक Google डूडलर की असली काली बिल्ली से प्रेरित था। पांच इशारे-आधारित जादू तेज़ गति पर बेहद कठिन हो जाते हैं। 2020 में एक लोकप्रिय सीक्वल, मैजिक कैट अकादमी 2, पानी के नीचे की सेटिंग के साथ आया।`,
    controls: 'हर आने वाले भूत के ऊपर दिखाए गए प्रतीक को बनाने के लिए माउस से क्लिक करके ड्रैग करें (या मोबाइल पर स्वाइप करें)। भूत को मोमो तक पहुंचने से पहले भगाने के लिए आकृति को सटीक रूप से मिलाएं। ऊंची मंजिलों पर भूत तेज़ हो जाते हैं और प्रतीक अधिक जटिल हो जाते हैं। अगर बहुत सारे भूत मोमो तक पहुंच गए तो खेल समाप्त हो जाता है।',
  },
  'garden-gnomes': {
    title: 'गार्डन गनोम Google Doodle गेम',
    description: `इस रंगीन Google Doodle में कैटापल्ट से गार्डन गनोम को जितना दूर हो सके उछालें।\n\nयह डूडल 10 जून, 2018 को जारी किया गया था, जो 19वीं सदी के जर्मनी की गार्डन गनोम परंपरा का जश्न मनाता है जहां माना जाता था कि वे सौभाग्य लाते हैं। इसकी भौतिकी इंजन को हर कैटापल्ट प्रयास को संतोषजनक और अप्रत्याशित बनाने के लिए सावधानीपूर्वक ट्यून किया गया था।`,
    controls: 'गनोम को गुलेल से लॉन्च करने के लिए क्लिक करें या स्पेस दबाएं। गुलेल का हाथ अपने आप घूमता है — अधिकतम दूरी के लिए सही कोण पर छोड़ें। हवा में होने पर, बूस्ट सक्रिय करने के लिए फिर से क्लिक करें। ट्रैम्पोलिन और फूलों से टकराकर उड़ान बढ़ाएं। अतिरिक्त दूरी के लिए बोनस टाइलों पर उतरने की कोशिश करें।',
  },
  'minesweeper': {
    title: 'Google माइनस्वीपर',
    description: `Google का क्लासिक माइनस्वीपर गेम खेलें। खानों को चिन्हित करें और बिना विस्फोट किए मैदान साफ़ करें।\n\nमाइनस्वीपर पहली बार 1990 में Windows 3.1 पर दिखा और मूल रूप से उपयोगकर्ताओं को लेफ्ट-क्लिक और राइट-क्लिक सिखाने के लिए डिज़ाइन किया गया था। Google का आधुनिक संस्करण मूल की संतोषजनक तर्कशक्ति को बनाए रखते हुए स्वच्छ दृश्य और मोबाइल के लिए उत्तरदायी टच नियंत्रण जोड़ता है।`,
    controls: 'टाइल को खोलने के लिए लेफ्ट-क्लिक करें। संख्याएं बताती हैं कि उस टाइल के आसपास कितनी माइन हैं। जिस टाइल में माइन होने का संदेह हो उस पर फ्लैग लगाने के लिए राइट-क्लिक करें (या मोबाइल पर लॉन्ग-प्रेस करें)। संख्याओं का उपयोग करके पता लगाएं कि कौन सी टाइलें सुरक्षित हैं। सभी गैर-माइन टाइलें खोलकर जीतें। माइन पर क्लिक करने से खेल तुरंत समाप्त हो जाता है।',
  },
  'tic-tac-toe': {
    title: 'Google टिक-टैक-टो',
    description: `Google की AI को क्लासिक टिक-टैक-टो में चुनौती दें। तीन एक लाइन में लगाकर जीतें।\n\nटिक-टैक-टो हजारों वर्षों से खेला जा रहा है, प्राचीन मिस्र में 1300 ईसा पूर्व के साक्ष्य मिले हैं। गणितीय रूप से 255,168 संभावित खेल अनुक्रम हैं। Google की 'असंभव' AI एक परफेक्ट मिनिमैक्स रणनीति का उपयोग करती है जिसे कभी हराया नहीं जा सकता।`,
    controls: '3×3 ग्रिड पर किसी भी खाली वर्ग पर क्लिक करके अपना चिह्न (X या O) लगाएं। अपने प्रतिद्वंद्वी के साथ बारी-बारी खेलें। जो खिलाड़ी पहले अपने तीन चिह्न एक पंक्ति में लगा ले — क्षैतिज, ऊर्ध्वाधर, या विकर्ण रूप से — वह जीतता है। शुरू करने से पहले कठिनाई स्तर चुनें: आसान में जीतना सरल है, असंभव में परफेक्ट AI रणनीति होती है।',
  },
  'feud': {
    title: 'Google Feud गेम',
    description: `Google की सबसे लोकप्रिय सर्च को इस लत लगाने वाले ऑटोकम्पलीट गेम में अनुमान लगाएँ। क्या आप सही जवाब दे पाएँगे?\n\nFamily Feud टीवी शो से प्रेरित, Google Feud वास्तविक ऑटोकम्पलीट डेटा का उपयोग करता है, जो हर राउंड को सामूहिक मानवीय जिज्ञासा की एक रोचक झलक बनाता है। चार श्रेणियों — संस्कृति, लोग, नाम और प्रश्न — में से प्रत्येक बिल्कुल अलग प्रकार की खोजें प्रस्तुत करती है।`,
    controls: 'टेक्स्ट बॉक्स में अपना जवाब टाइप करें और सबमिट करने के लिए एंटर दबाएं। आपको दिए गए प्रॉम्प्ट के लिए सबसे आम Google ऑटोकम्प्लीट सुझाव अनुमान लगाने हैं। शीर्ष उत्तर सबसे अधिक अंक देते हैं। प्रत्येक प्रश्न में आपको तीन गलत अनुमान मिलते हैं, उसके बाद राउंड समाप्त हो जाता है। सोचें कि आम लोग क्या खोजेंगे।',
  },
  'halloween': {
    title: 'हैलोवीन गूगल डूडल — भूतों का त्योहार',
    description: `इस इंटरैक्टिव Google Doodle के साथ Halloween मनाएँ। जादूगर बिल्ली की मदद से भूतों को हराएँ।\n\nग्रेट घूल ड्यूअल 30 अक्टूबर, 2018 को जारी किया गया था और Google का पहला मल्टीप्लेयर डूडल गेम बना, जिसमें आठ खिलाड़ी एक साथ प्रतिस्पर्धा कर सकते थे। 2021 में बेहतर मैचमेकिंग और अतिरिक्त मानचित्रों के साथ एक रिबूटेड संस्करण आया।`,
    controls: 'अपने भूत को हॉन्टेड मैप पर चलाने के लिए एरो कीज़ या WASD का उपयोग करें। आत्मा की लौ इकट्ठा करने के लिए उन पर से गुज़रें, फिर उन्हें जमा करने के लिए अपनी टीम की कड़ाही पर लौटें। दुश्मन भूतों से टकराकर उनकी इकट्ठा की हुई लौ चुराएं। टाइमर समाप्त होने पर जिस टीम ने सबसे अधिक लौ जमा की हो वह जीतती है। अकेले या दोस्तों के साथ ऑनलाइन खेलें।',
  },
  'boba-bubble-tea': {
    title: 'Boba Tea गेम Google',
    description: `इस प्यारे Google Doodle में स्वादिष्ट बोबा बबल टी बनाएँ। सामग्री मिलाएँ और ड्रिंक सर्व करें।\n\nयह डूडल 30 जनवरी, 2024 को जारी किया गया था जब ताइवान ने बबल टी को अपनी पाक विरासत का महत्वपूर्ण हिस्सा मान्यता दी। मशीन लर्निंग-संचालित फ्लेवर कहानियां वास्तविक चाय-जोड़ी सिद्धांतों पर आधारित हैं और हजारों संभावित संयोजन अनोखी कहानियां बनाते हैं।`,
    controls: 'अपनी बोबा ड्रिंक को कस्टमाइज़ करने के लिए प्रत्येक सामग्री विकल्प पर क्लिक करें या टैप करें। हर चरण में काम करें: अपना चाय बेस चुनें, फिर दूध का प्रकार, मिठास का स्तर, बर्फ की मात्रा, और अंत में अपनी टॉपिंग। हर चयन के बाद, डूडल आपकी पसंद को एनिमेट करता है। पूरा होने पर, आपकी व्यक्तिगत बोबा कहानी सामने आती है। नई कहानियों के लिए अलग-अलग संयोजन आज़माएं!',
  },
  'google-cat-game': {
    title: 'Google Cat गेम',
    description: `Google की प्यारी बिल्ली के साथ इस मज़ेदार इंटरैक्टिव एडवेंचर में खेलें। पज़ल सुलझाएँ और खोजबीन करें।\n\nयह मूल 2016 का मैजिक कैट अकादमी हैलोवीन डूडल है जिसने पहली बार बिल्ली जादूगर मोमो को दुनिया से परिचित कराया। सरल रेखाओं से लेकर जटिल बहु-स्ट्रोक पैटर्न तक, यह माउस निपुणता की एक आश्चर्यजनक रूप से तीव्र परीक्षा है।`,
    controls: 'हर भूत को मोमो तक पहुंचने से पहले उसके ऊपर दिखाए गए प्रतीक को बनाने के लिए क्लिक करके ड्रैग करें। नए भूत प्रकारों के लिए अधिक जटिल प्रतीकों की आवश्यकता होती है — दिखाई गई आकृति पर ध्यान दें। कुछ भूत दूसरों से तेज़ चलते हैं। अगर बहुत सारे भूत बिना भगाए मोमो तक पहुंच गए तो खेल समाप्त हो जाता है। सटीकता उतनी ही मायने रखती है जितनी गति।',
  },
  'no-internet-dinosaur-game-google-chrome-dino': {
    title: 'नो इंटरनेट डायनासोर गेम (Google Chrome Dino)',
    description: `Chrome का मशहूर डायनासोर गेम। कैक्टस पर कूदें और टेरोडैक्टिल से बचते हुए अंतहीन दौड़ लगाएँ।\n\nChrome UX डिज़ाइनर सेबेस्टियन गेब्रियल ने 2014 में इसे बिना इंटरनेट के प्रागैतिहासिक युग की मज़ाकिया याद के रूप में बनाया था। 700 अंकों पर छिपा नाइट मोड सक्रिय होता है। इसे प्रति माह अनुमानित 270 मिलियन बार खेला जाता है, जो इसे अब तक के सबसे ज़्यादा खेले जाने वाले गेम में से एक बनाता है।`,
    controls: 'कैक्टस के ऊपर कूदने के लिए स्पेस दबाएं या ऊपर का एरो दबाएं। टेरोडैक्टिल के नीचे से गुज़रने के लिए नीचे का एरो दबाएं। मोबाइल पर, कूदने के लिए स्क्रीन टैप करें। खेल धीरे शुरू होता है और आपके स्कोर बढ़ने के साथ लगातार तेज़ होता जाता है। T-Rex के किसी बाधा से टकराने पर खेल समाप्त हो जाता है। गेम ओवर के बाद स्पेस दबाकर फिर से शुरू करें।',
  },
  't-rex-run-3d': {
    title: 'T-Rex Run 3D — टी-रेक्स 3D दौड़',
    description: `Chrome के डायनासोर गेम को 3D में अनुभव करें। रोमांचक 3D ग्राफ़िक्स के साथ बाधाओं से बचते हुए दौड़ें।\n\nयह प्रशंसक-निर्मित 3D रीइमेजिनिंग मूल फ्लैट साइड-स्क्रॉलिंग गेम को डायनासोर के पीछे के परिप्रेक्ष्य में बदल देती है जिसमें टेक्स्चर्ड भूभाग और वॉल्यूमेट्रिक बादल हैं। अनौपचारिक होने के बावजूद, इसने सर्वश्रेष्ठ Chrome Dino मॉड्स में से एक के रूप में बड़ी संख्या में प्रशंसक बनाए हैं।`,
    controls: 'कैक्टस और बाधाओं के ऊपर कूदने के लिए स्पेस दबाएं या ऊपर का एरो दबाएं। नीचे झुकने के लिए नीचे का एरो दबाएं। 3D मोड में, बाधाएं क्षितिज से आती हैं — कूदने से पहले उनकी दूरी का ध्यानपूर्वक अनुमान लगाएं। समय के साथ खेल तेज़ होता जाता है। मोबाइल पर, कूदने के लिए टैप करें और झुकने के लिए नीचे स्वाइप करें। टकराव के बाद स्पेस दबाकर फिर से शुरू करें।',
  },
  'google-santa-tracker': {
    title: 'गूगल सांता ट्रैकर — सांता खोजक',
    description: `Google Santa Tracker के साथ सांता क्लॉज़ की यात्रा को ट्रैक करें। मज़ेदार गेम खेलें और क्रिसमस का आनंद लें।\n\nGoogle का सांता ट्रैकर 2004 से एक छुट्टी परंपरा रहा है, जो एक साधारण मानचित्र ट्रैकर से दर्जनों गतिविधियों वाले पूर्ण उत्सवी अनुभव में विकसित हुआ है। क्रिसमस की पूर्व संध्या पर, ट्रैकर लाइव मोड में बदल जाता है और सभी 24 समय क्षेत्रों में सांता की यात्रा दिखाता है।`,
    controls: 'विभिन्न मिनी-गेम और गतिविधियां शुरू करने के लिए त्योहारी गांव में इमारतों और किरदारों पर क्लिक करें। हर मिनी-गेम के अपने नियंत्रण स्क्रीन पर दिखाए जाते हैं। मैप को नेविगेट करने और विभिन्न स्थानों की खोज के लिए माउस का उपयोग करें। क्रिसमस की पूर्व संध्या पर, इंटरैक्टिव विश्व मानचित्र पर सांता के रीयल-टाइम मार्ग को देखें।',
  },
  'blob-opera-google-game': {
    title: 'Blob Opera Google गेम',
    description: `रंग-बिरंगे ब्लॉब कैरेक्टर्स के साथ ओपेरा संगीत बनाएँ। बिना किसी अनुभव के सुंदर हार्मनी बनाएँ।\n\nकलाकार डेविड ली ने Google Arts & Culture के सहयोग से इसे बनाया, जो चार असली ओपेरा गायकों — टेनर, बास, मेज़ो-सोप्रानो और सोप्रानो — की 100 घंटे से अधिक की रिकॉर्डिंग पर प्रशिक्षित मशीन लर्निंग मॉडल का उपयोग करता है। 15 दिसंबर, 2020 को जारी, यह तुरंत वायरल क्रिएटिव सनसनी बन गया।`,
    controls: 'किसी भी ब्लॉब को ऊपर या नीचे क्लिक करके ड्रैग करें ताकि उसकी पिच बदले — ऊपर ड्रैग करने पर ऊंची नोट बजती है। स्वर ध्वनि बदलने के लिए बाएं या दाएं ड्रैग करें। चारों ब्लॉब रीयल-टाइम में अपने आप सामंजस्य बनाते हैं। समृद्ध कॉर्ड के लिए एक साथ कई ब्लॉब ड्रैग करके देखें। अपनी रचना रिकॉर्ड करने और साझा करने के लिए रिकॉर्ड बटन दबाएं।',
  },
  'space-invaders-google': {
    title: 'स्पेस इनवेडर्स — गूगल',
    description: `क्लासिक Space Invaders को Google स्टाइल में खेलें। एलियन आक्रमणकारियों को नष्ट करें और पृथ्वी की रक्षा करें।\n\nमूल Space Invaders 1978 में तोमोहिरो निशिकाडो ने बनाया था और जापान में इतना लोकप्रिय हुआ कि सिक्कों की कमी हो गई। इस गेम ने हाई-स्कोर सिस्टम, प्रगतिशील कठिनाई और दुश्मन हमले के पैटर्न जैसी कई पहली चीज़ें आर्केड उद्योग में पेश कीं।`,
    controls: 'अपनी तोप को चलाने के लिए बाएं और दाएं एरो कीज़ का उपयोग करें। फायर करने के लिए स्पेस दबाएं। सभी एलियन आक्रमणकारियों को स्क्रीन के नीचे पहुंचने से पहले मार गिराएं। विनाशकारी बैरियर के पीछे छिपें — लेकिन ध्यान रखें कि हर हिट से वे घिसते हैं। दुर्लभ UFO बोनस शिप अतिरिक्त अंकों के लिए स्क्रीन के ऊपर से गुज़रता है अगर आप इसे मार सकें।',
  },
  'celebrating-petanque': {
    title: 'Pétanque उत्सव',
    description: `इस Google Doodle में फ़्रांसीसी खेल पेटैंक का आनंद लें। गेंद को लक्ष्य के करीब फेंकें और अंक जीतें।\n\nयह डूडल पेटैंक की समृद्ध परंपरा का जश्न मनाता है, जो 1907 में प्रोवेंस, फ़्रांस में उत्पन्न हुआ और तब से दुनिया भर के पार्कों में एक प्रिय शगल बन गया है। भौतिकी सिमुलेशन बजरी पर धातु की बॉल के वजन और रोल को सटीक रूप से मॉडल करता है।`,
    controls: 'अपना थ्रो एम करने के लिए क्लिक करके होल्ड करें, फिर पावर सेट करने के लिए ड्रैग करें। कोशोनेट (छोटी लक्ष्य गेंद) की ओर अपनी बॉल फेंकने के लिए छोड़ें। अपनी बॉल को प्रतिद्वंद्वी की बॉल से कोशोनेट के करीब रखने की कोशिश करें। सटीक निशाने से आप उनकी बॉल को दूर भी भगा सकते हैं। कोशोनेट के सबसे करीब वाला खिलाड़ी अंक प्राप्त करता है।',
  },
  'dino-swords': {
    title: 'डिनो स्वॉर्ड्स — डायनासोर तलवारें',
    description: `Chrome डायनासोर गेम को हथियारों के साथ खेलें। तलवारें और अन्य हथियार इकट्ठा करके बाधाओं को नष्ट करें।\n\nDino Swords को MSCHF ने बनाया था, जो कई वायरल इंटरनेट कला परियोजनाओं के पीछे का रचनात्मक समूह है। इसमें 26 से अधिक विभिन्न हथियार हैं — जिनमें नुनचक, लाइटसेबर और ग्रैविटी गन शामिल हैं — प्रत्येक अद्वितीय हमले एनिमेशन के साथ। यह सोशल मीडिया पर वायरल हो गया।`,
    controls: 'कूदने के लिए स्पेस दबाएं या ऊपर का एरो दबाएं। झुकने के लिए नीचे का एरो दबाएं। जब दुश्मन सीमा में होते हैं तो आपका मौजूदा हथियार अपने आप सक्रिय हो जाता है। दौड़ के दौरान दिखने वाले हथियार पावर-अप इकट्ठा करके अपना शस्त्रागार बदलें। हर हथियार अलग तरह से काम करता है — तलवारें काटती हैं, ढालें रोकती हैं, लेज़र गन अपने आप फायर करती हैं। मरने के बाद स्पेस दबाकर फिर से शुरू करें।',
  },
  'doodle-scoville': {
    title: 'डूडल स्कोविल — मिर्ची का पैमाना',
    description: `इस Google Doodle में मिर्ची की तीखापन चुनौती स्वीकार करें। आइसक्रीम से मिर्चियों को हराएँ।\n\nयह डूडल 22 जनवरी, 2016 को फार्मासिस्ट विल्बर स्कोविल की 151वीं जयंती पर जारी किया गया था, जिन्होंने 1912 में मिर्ची की तीखापन मापने का स्कोविल टेस्ट विकसित किया। गेम में बेल पेपर से लेकर कैरोलाइना रीपर तक वास्तविक मिर्चों को उनकी स्कोविल हीट यूनिट्स के अनुसार व्यवस्थित किया गया है।`,
    controls: 'प्रतिद्वंद्वी मिर्च पर आइसक्रीम स्कूप फेंकने के लिए क्लिक करें या टैप करें। माउस हिलाकर या एरो कीज़ का उपयोग करके अपनी मिर्च को बाएं और दाएं चलाएं। प्रतिद्वंद्वी के हमलों से बचते हुए अपने वार करें। हर प्रतिद्वंद्वी पिछले से अधिक तीखा और कठिन होता है। उन्हें पर्याप्त बार मारकर हराएं और स्कोविल स्केल पर आगे बढ़ें।',
  },
  'doodle-valentines-day': {
    title: 'Doodle वैलेंटाइन डे',
    description: `इस प्यारे Google Doodle के साथ वैलेंटाइन डे मनाएँ। रोमांटिक पज़ल में जोड़ियाँ बनाएँ।\n\n14 फ़रवरी, 2017 को जारी इस डूडल में पैंगोलिन — दुनिया के सबसे लुप्तप्राय जानवरों में से एक — को वन्यजीव संरक्षण के बारे में जागरूकता बढ़ाने के लिए चुना गया। चार स्तर अलग-अलग पारिस्थितिकी तंत्रों का प्रतिनिधित्व करते हैं और Google ने संरक्षण संगठनों के साथ साझेदारी की।`,
    controls: 'दृश्य के साथ इंटरैक्ट करने और पैंगोलिन को उसकी डिलीवरी यात्रा पर मार्गदर्शन करने के लिए क्लिक करें या टैप करें। एनिमेशन ट्रिगर करने और कहानी आगे बढ़ाने के लिए हाइलाइट की गई वस्तुओं और किरदारों पर क्लिक करें। खेल मुख्य रूप से पॉइंट-एंड-क्लिक है — हर दृश्य की खोज करें और आगे का रास्ता खोजने तथा सभी उपहार पहुंचाने के लिए जो भी दिखे उससे इंटरैक्ट करें।',
  },
  'pony-express': {
    title: 'पोनी एक्सप्रेस — घोड़ा डाक',
    description: `इस Google Doodle में पोनी एक्सप्रेस राइडर बनें। घोड़े पर सवार होकर पत्र इकट्ठा करें और बाधाओं से बचें।\n\n14 अप्रैल, 2015 को जारी यह डूडल पोनी एक्सप्रेस की 155वीं वर्षगाँठ मनाता है, जो 1860-1861 में केवल 18 महीने चला लेकिन अमेरिका की सबसे स्थायी सीमांत किंवदंतियों में से एक बन गया। पांच चरण सेंट जोसेफ, मिसूरी से सैक्रामेंटो, कैलिफ़ोर्निया तक के वास्तविक मार्ग को दर्शाते हैं।`,
    controls: 'चट्टानों, बाड़ों और नदियों जैसी बाधाओं पर कूदने के लिए स्पेस दबाएं या क्लिक करें। बोनस अंकों के लिए रास्ते में दिखने वाली डाक थैलियां इकट्ठा करें। आपका घोड़ा अपने आप दौड़ता है — अपनी कूद का समय सही रखने पर ध्यान दें। बहुत सारी डाक थैलियां छूटने या बाधाओं से टकराने पर गति धीमी हो जाती है। सवारी पूरी करने के लिए सभी पांच चरण पूरे करें।',
  },
  'doodle-jump-2': {
    title: 'डूडल जम्प 2',
    description: `लोकप्रिय Doodle Jump का दूसरा भाग खेलें। प्लेटफ़ॉर्म पर कूदते हुए ऊँचाई पर पहुँचें।\n\nमूल Doodle Jump 2009 में लॉन्च हुआ और अब तक के सबसे अधिक बिकने वाले मोबाइल गेम में से एक बन गया। इस सीक्वल में कई थीम वाली दुनियाएं हैं, प्रत्येक में अद्वितीय खतरे और पावर-अप। हाथ से बनाई कला शैली को सुरक्षित रखते हुए काफ़ी अधिक दृश्य विविधता जोड़ी गई है।`,
    controls: 'डूडलर को बगल में चलाने के लिए अपना डिवाइस झुकाएं या बाएं/दाएं एरो कीज़ का उपयोग करें। प्लेटफॉर्म पर उतरने पर डूडलर अपने आप कूदता है। ऊपर के राक्षसों पर शूट करने के लिए टैप करें या क्लिक करें। नई सुविधाओं में वॉल-जंपिंग (दीवारों से उछलना) और विशेष प्लेटफॉर्म प्रकार शामिल हैं। जेटपैक, स्प्रिंग और शील्ड के लिए पावर-अप इकट्ठा करके ऊंचाई पर चढ़ें।',
  },
  'doodle-history-of-pizza': {
    title: 'Doodle पिज़्ज़ा का इतिहास',
    description: `इस Google Doodle में पिज़्ज़ा का इतिहास जानें। पिज़्ज़ा स्लाइस काटकर ऑर्डर पूरे करें।\n\nयह डूडल सिखाता है कि पिज़्ज़ा का इतिहास हज़ारों साल पुराना है — प्राचीन मिस्र में फ्लैटब्रेड पकाने से लेकर 1889 में रैफेल एस्पोसितो द्वारा रानी मार्गेरीटा के लिए मार्गेरीटा पिज़्ज़ा बनाने तक। प्रत्येक युग का मिनी-गेम उस समय की खाना पकाने की तकनीकों को प्रतिबिंबित करता है।`,
    controls: 'हर पिज़्ज़ा बनाने के चरण में इंटरैक्ट करने के लिए क्लिक करके ड्रैग करें। आटा गूंधने के लिए तेज़ी से क्लिक करें, सॉस फैलाने के लिए ड्रैग करें, और टॉपिंग रखने के लिए उन पर क्लिक करें। हर ऐतिहासिक युग में थोड़े अलग मिनी-गेम मैकेनिक्स होते हैं जो ऑन-स्क्रीन प्रॉम्प्ट में दिखाए जाते हैं। पिज़्ज़ा सफलतापूर्वक पूरा करने और इतिहास में आगे बढ़ने के लिए हर चरण के निर्देशों का पालन करें।',
  },
  'doodle-kids-coding': {
    title: 'Doodle किड्स कोडिंग',
    description: `इस Google Doodle में बच्चों के लिए कोडिंग सीखें। ब्लॉक कमांड से खरगोश को गाजर तक पहुँचाएँ।\n\n4 दिसंबर, 2017 को जारी यह डूडल MIT की Scratch टीम के साथ विकसित किया गया था और 1960 के दशक में सीमोर पेपर्ट द्वारा बनाई Logo प्रोग्रामिंग भाषा से प्रेरित है। इसके छह स्तर अनुक्रमण, लूप और कंडीशनल्स सिखाते हैं और शिक्षकों द्वारा दुनिया भर की कक्षाओं में इसे शिक्षण उपकरण के रूप में अपनाया गया है।`,
    controls: 'अपने अनुक्रम में जोड़ने के लिए बाईं ओर के कमांड ब्लॉक्स पर क्लिक करें। डूडल बनी को गाजर तक पहुंचाने के लिए ब्लॉक्स को सही क्रम में व्यवस्थित करें। अपना प्रोग्राम चलाने के लिए प्ले बटन क्लिक करें और देखें कि बनी आपके निर्देशों का पालन करती है या नहीं। अगर बनी अटक जाए तो अपने अनुक्रम में बदलाव करें और फिर से कोशिश करें। हर पहेली लूप और कंडीशनल जैसी नई कोडिंग अवधारणाएं सिखाती है।',
  },
  'doodle-valentines-day-2022': {
    title: 'Doodle वैलेंटाइन डे 2022',
    description: `2022 के वैलेंटाइन डे Google Doodle में प्यारे मिनी-गेम खेलें और प्रेम का जश्न मनाएँ।\n\nGoogle के वैलेंटाइन डे डूडल 2011 से एक वार्षिक परंपरा बन गए हैं। 2022 संस्करण में हेजहॉग को चुना गया क्योंकि वास्तविक हेजहॉग एकांतप्रिय प्राणी हैं जो केवल संभोग के मौसम में साथी की तलाश करते हैं। जलरंग कला शैली हर स्तर को एक कहानी की किताब जैसी गुणवत्ता देती है।`,
    controls: 'हर लेवल में हेजहॉग को चलाने के लिए एरो कीज़ या WASD का उपयोग करें। स्टेज पर बिखरे फूल और दिल इकट्ठा करें। अपने रास्ते में आने वाली बाधाओं और खतरों से बचें। प्रेम कहानी आगे बढ़ाने के लिए हर लेवल के अंत में लक्ष्य तक पहुंचें। मोबाइल पर, नेविगेट करने के लिए ऑन-स्क्रीन दिशात्मक बटनों का उपयोग करें।',
  },
  'doodle-ludwig-van-beethovens-245th-year': {
    title: 'Doodle Ludwig Van Beethoven की 245वीं जयंती',
    description: `Beethoven की 245वीं जयंती के इस Google Doodle में उनकी प्रसिद्ध धुनों के टुकड़े सही क्रम में जोड़ें।\n\n17 दिसंबर, 2015 को जारी इस डूडल में बीथोवन की पांचवीं सिम्फनी और Für Elise सहित चार प्रतिष्ठित रचनाएं हैं। एनिमेटेड कटसीन बीथोवन के बढ़ते बहरेपन और 44 वर्ष की आयु तक पूरी तरह बहरे होने के बावजूद संगीत रचना जारी रखने के उनके उल्लेखनीय दृढ़ संकल्प को दर्शाते हैं।`,
    controls: 'स्क्रीन पर तैरते हुए बिखरे संगीत पृष्ठों को इकट्ठा करने के लिए क्लिक करें या टैप करें। हर रचना को पुनर्निर्माण करने के लिए पृष्ठों को सही क्रम में इकट्ठा करना होगा — सही अनुक्रम जानने के लिए संगीत संकेतों को सुनें। बीथोवन की कृति का हर अंश पूरा करने के लिए सभी पृष्ठ बहने से पहले इकट्ठा करें।',
  },
  'doodle-earth-day-2020': {
    title: 'Doodle पृथ्वी दिवस 2020',
    description: `पृथ्वी दिवस 2020 के इस Google Doodle में मधुमक्खी बनकर फूलों को परागित करें और पर्यावरण बचाएँ।\n\nपृथ्वी दिवस पहली बार 22 अप्रैल, 1970 को मनाया गया था और 2020 का यह डूडल 50वीं वर्षगाँठ के अवसर पर बनाया गया था। प्रत्येक पारिस्थितिकी तंत्र दृश्य में पर्यावरण शोधकर्ताओं द्वारा सत्यापित वैज्ञानिक तथ्य हैं। यह वैश्विक लॉकडाउन के दौरान जारी किया गया था, जो प्रकृति की एक आभासी खिड़की प्रदान करता था।`,
    controls: 'विभिन्न पारिस्थितिकी तंत्र दृश्यों के बीच नेविगेट करने के लिए तीरों पर क्लिक करें या स्वाइप करें। उस पारिस्थितिकी तंत्र के बारे में दिलचस्प तथ्य जानने के लिए हर दृश्य में जानवरों, पौधों और पर्यावरणीय तत्वों पर क्लिक करें। हर वातावरण की खोज में समय लगाएं — हर दृश्य में छिपे इंटरैक्टिव तत्व हैं। पारिस्थितिकी तंत्रों के बीच जाने के लिए नेविगेशन डॉट्स का उपयोग करें।',
  },
  'doctor-who-doodle': {
    title: 'डॉक्टर हू डूडल',
    description: `Doctor Who के इस Google Doodle गेम में टाइम ट्रैवल करें। दुश्मनों से बचें और सभी स्तर पार करें।\n\n23 नवंबर, 2013 को Doctor Who की 50वीं वर्षगाँठ पर जारी, इस डूडल में डॉक्टर के सभी ग्यारह अवतार खेलने योग्य हैं। हर समय काल में डालेक, साइबरमेन और वीपिंग एंजेल्स जैसे क्लासिक खलनायक हैं। यह BBC के साथ सहयोग से बना कुछ आधिकारिक लाइसेंस प्राप्त डूडल में से एक है।`,
    controls: 'हर समय काल में डॉक्टर को चलाने के लिए एरो कीज़ का उपयोग करें। वस्तुओं और किरदारों से इंटरैक्ट करने के लिए Z दबाएं या क्लिक करें। डालेक और अन्य दुश्मनों से बचते हुए चुराए गए Google अक्षर इकट्ठा करें। हर युग में अनोखी पहेलियां हैं — अच्छी तरह खोजें और हर चीज़ से इंटरैक्ट करें। एक लेवल के सभी अक्षर इकट्ठा करने के बाद TARDIS पर लौटें।',
  },
  'birth-of-hip-hop-doodle-game': {
    title: 'हिप हॉप की शुरुआत Doodle गेम',
    description: `इस Google Doodle में हिप हॉप संगीत के जन्म का जश्न मनाएँ। टर्नटेबल पर बीट्स मिक्स करें और DJ बनें।\n\n11 अगस्त, 2017 को जारी यह डूडल DJ Kool Herc की 1973 की ब्रॉन्क्स ब्लॉक पार्टी की याद में बनाया गया था जिसे हिप-हॉप का जन्म माना जाता है। कथन हिप-हॉप किंवदंती Fab 5 Freddy ने किया था और इसे हिप-हॉप समुदाय से दुनिया भर में प्रशंसा मिली।`,
    controls: 'रिकॉर्ड को आगे-पीछे स्क्रैच करने के लिए क्लिक करें। ट्रैक पर ड्रम सैंपल लेयर करने के लिए बीट पैड पर क्लिक करें। हर एलिमेंट की वॉल्यूम एडजस्ट करने के लिए मिक्सर स्लाइडर का उपयोग करें। सैंपल सेट बदलने के लिए अलग-अलग विनाइल रिकॉर्ड पर क्लिक करें। अपना पूरा मिक्स सुनने के लिए प्ले बटन दबाएं। अपनी खुद की अनोखी हिप-हॉप बीट बनाने के लिए अलग-अलग संयोजन आज़माएं।',
  },
  'doodle-celebrating-loteria': {
    title: 'Doodle Lotería उत्सव',
    description: `इस Google Doodle में मैक्सिकन Lotería कार्ड गेम खेलें। कार्ड मिलाएँ और जीतने के लिए "लोतेरीया!" बोलें।\n\nलोतेरीया 18वीं सदी से मैक्सिको की एक प्रिय परंपरा है, मूल रूप से इटली से लाई गई और मैक्सिकी कल्पना के साथ अनुकूलित। 2019 का यह डूडल रीयल-टाइम मल्टीप्लेयर गेमप्ले वाला पहला डूडल था। कार्ड चित्रण पांच मैक्सिकी अतिथि कलाकारों ने बनाए।`,
    controls: 'कॉलर को एक-एक करके कार्ड दिखाते हुए देखें। जब कोई दिखाया गया कार्ड आपके बोर्ड की टाइल से मेल खाए, तो उसे चिह्नित करने के लिए उस टाइल पर क्लिक करें। एक पंक्ति, स्तंभ या विकर्ण में चार चिह्नित टाइलें लगाकर जीतें और "लोटेरिया!" चिल्लाएं। आप AI प्रतिद्वंद्वियों या दोस्तों के साथ खेल सकते हैं। खेल तेज़ी से चलता है — ध्यान केंद्रित रखें और अपनी टाइलें तुरंत चिह्नित करें।',
  },
  'basketball-2012-google-doodle': {
    title: 'बास्केटबॉल 2012 Google Doodle',
    description: `2012 ओलंपिक के इस Google Doodle में बास्केटबॉल शूट करें। सही समय पर गेंद फेंककर अधिकतम अंक बनाएँ।\n\nयह लंदन 2012 ग्रीष्मकालीन ओलंपिक के दौरान जारी इंटरैक्टिव खेल डूडल श्रृंखला का हिस्सा था। शूटिंग मैकेनिक वास्तविक फ्री-थ्रो भौतिकी की अनुभूति से प्रेरित था। प्रतिस्पर्धी खिलाड़ियों ने पाया कि मध्यम शक्ति पर 45-डिग्री रिलीज़ कोण सबसे विश्वसनीय परिणाम देता है।`,
    controls: 'अपना शॉट एम करने के लिए गेंद पर ऊपर की ओर क्लिक करके ड्रैग करें, फिर फेंकने के लिए छोड़ें। आपके ड्रैग की दिशा और लंबाई शॉट के कोण और शक्ति को निर्धारित करती है। एक शॉट आर्क गाइड आपको निशाना लगाने में मदद करता है। शॉट क्लॉक खत्म होने से पहले स्कोर करें। लगातार सफल शॉट से मल्टीप्लायर मिलते हैं — अधिकतम अंकों के लिए अपनी स्ट्रीक जारी रखें।',
  },
  'celebrating-lake-xochimilco': {
    title: 'Lake Xochimilco उत्सव',
    description: `इस Google Doodle में मैक्सिको की Lake Xochimilco और एक्सोलोटल के बारे में जानें। जलीय जीवों की खोज करें।\n\nएक्सोलोटल एक गंभीर रूप से लुप्तप्राय प्रजाति है जो केवल ज़ोचिमिल्को की नहरों में पाई जाती है, जंगल में 1,000 से कम बचे हैं। यह डूडल चिनंपास को उजागर करता है — एज़्टेक द्वारा 500 वर्ष पहले बनाए गए कृत्रिम तैरते द्वीप जो अब तक की सबसे टिकाऊ कृषि प्रणालियों में से एक हैं।`,
    controls: 'एक्सोलोटल को जलमार्गों में तैराने के लिए एरो कीज़ या WASD का उपयोग करें। पानी में तैरती खाद्य वस्तुएं इकट्ठा करके अंक प्राप्त करें। एक्सोलोटल को नुकसान पहुंचाने वाले शिकारियों और बाधाओं से बचें। चिनंपास के बीच संकरे रास्तों से सावधानी से गुज़रें। लेक ज़ोचिमिल्को पारिस्थितिकी तंत्र के बारे में दिलचस्प तथ्य जानने के लिए दिखने वाले सूचना बुलबुलों पर क्लिक करें।',
  },
  'celebrating-johann-sebastian-bach': {
    title: 'Johann Sebastian Bach उत्सव',
    description: `इस Google Doodle में Bach शैली में संगीत बनाएँ। AI आपकी धुन को Bach की हार्मनी में बदल देगा।\n\n21 मार्च, 2019 को बाख की 334वीं जयंती पर जारी, यह AI को मुख्य तंत्र के रूप में उपयोग करने वाला पहला Google डूडल था। Coconet मशीन लर्निंग मॉडल बाख के 306 कोरल हार्मोनाइज़ेशन पर प्रशिक्षित है और दो सेकंड से कम में किसी भी धुन को हार्मोनाइज़ कर सकता है।`,
    controls: 'संगीत स्टाफ पर नोट्स रखने और एक धुन बनाने के लिए क्लिक करें। पिच सेट करने के लिए किसी भी लाइन या स्पेस पर नोट्स रखें, और मौजूदा नोट्स हटाने के लिए उन पर क्लिक करें। अपनी धुन बनाने के बाद, हार्मोनाइज़ बटन क्लिक करें और Google की AI आपकी धुन के चारों ओर बाख-शैली का पूरा हार्मोनाइज़ेशन बनाएगी। परिणाम सुनने के लिए प्ले क्लिक करें, फिर साझा करें या नई धुन आज़माएं।',
  },
  'doodle-clara-rockmore': {
    title: 'डूडल क्लारा रॉकमोर',
    description: `Clara Rockmore के इस Google Doodle में थेरेमिन बजाएँ। हाथ हिलाकर सुंदर इलेक्ट्रॉनिक संगीत बनाएँ।\n\n9 मार्च, 2016 को क्लारा रॉकमोर की 105वीं जयंती पर जारी, यह डूडल थेरेमिन का विश्वसनीय अनुकरण करता है — एकमात्र वाद्य यंत्र जो बिना शारीरिक संपर्क के बजाया जाता है। क्लारा लिथुआनिया की एक बाल प्रतिभा वायलिनवादक थीं जो 1920 के दशक में थेरेमिन के आविष्कारक लियोन थेरेमिन से मिलने के बाद इस वाद्य से मोहित हो गईं।`,
    controls: 'थेरेमिन की पिच बदलने के लिए माउस को ऊपर-नीचे हिलाएं — ऊंची स्थिति का मतलब ऊंची पिच। वॉल्यूम एडजस्ट करने के लिए बाएं-दाएं हिलाएं। नोट बनाए रखने के लिए क्लिक करके होल्ड करें। गाइड के रूप में नोट्स हाइलाइट देखने के लिए मेनू से क्लारा रॉकमोर की किसी रचना का चयन करें। मोबाइल पर, बजाने के लिए अपनी उंगली स्क्रीन पर चलाएं।',
  },
  'mothers-day-2013-doodle': {
    title: 'मदर्स डे 2013 Doodle',
    description: `2013 के मदर्स डे Google Doodle में माँ के लिए प्यारा उपहार बनाएँ। क्राफ़्ट सामग्री से कुछ खास तैयार करें।\n\nGoogle 2000 से विशेष डूडल के साथ मदर्स डे मना रहा है। 2013 का संस्करण प्रत्यक्ष साझाकरण सुविधा शामिल करने वाले पहले डूडल में से एक था। हस्तनिर्मित चित्रण शैली एक बच्चे के घर में बनाए कार्ड की गर्माहट जगाने के लिए डिज़ाइन की गई थी।`,
    controls: 'कार्ड बनाने के चरणों में क्लिक करके आगे बढ़ें: पहले एक चित्रित दृश्य चुनें, फिर फूल और दिल जैसे सजावटी तत्वों पर क्लिक करके उन्हें चुनें। टेक्स्ट फील्ड में अपना व्यक्तिगत संदेश टाइप करें। लुक को कस्टमाइज़ करने के लिए कलर पैलेट का उपयोग करें। कार्ड से संतुष्ट होने पर, इसे ईमेल या सोशल मीडिया से भेजने के लिए शेयर बटन क्लिक करें।',
  },
  'mothers-day-2020-doodle': {
    title: 'मदर्स डे 2020 Doodle',
    description: `2020 के मदर्स डे Google Doodle में माँ के लिए एनिमेटेड कार्ड बनाएँ और प्यार भेजें।\n\nCOVID-19 महामारी के दौरान मई 2020 में जारी, इस डूडल ने विशेष प्रासंगिकता रखी जब लाखों परिवार एक साथ घरों में थे। एनिमेटेड विनेट सार्वभौमिक पालन-पोषण के क्षणों को दर्शाते हैं। Google ने बताया कि उपयोगकर्ताओं ने औसतन तीन मिनट इस इंटरैक्टिव कहानी के साथ बिताए।`,
    controls: 'एनिमेटेड कहानी में आगे बढ़ने के लिए क्लिक करें या टैप करें। इंटरैक्टिव क्षणों पर, ऑन-स्क्रीन प्रॉम्प्ट का पालन करें — आपको विशिष्ट वस्तुओं पर क्लिक करना, तत्वों को ड्रैग करना, या एनिमेशन की लय में टैप करना पड़ सकता है। इंटरैक्टिव अनुभागों के बीच कहानी अपने आप आगे बढ़ती है। अपने इनपुट के बीच आराम से बैठें और दिल छूने वाली यात्रा का आनंद लें।',
  },
  'doodle-crossword-puzzle': {
    title: 'Doodle क्रॉसवर्ड पज़ल',
    description: `इस Google Doodle में क्रॉसवर्ड पज़ल सुलझाएँ। सुराग पढ़ें और सही शब्द भरें।\n\nक्रॉसवर्ड पहेली का आविष्कार पत्रकार आर्थर विन ने किया था और पहली बार 21 दिसंबर, 1913 को प्रकाशित हुई। यह Google डूडल उस शताब्दी वर्षगाँठ का जश्न मनाता है जिसमें Google इतिहास और सामान्य ज्ञान दोनों का संदर्भ देने वाले चतुर सुराग शामिल हैं।`,
    controls: 'क्रॉसवर्ड ग्रिड में किसी वर्ग पर क्लिक करके उसे चुनें, फिर कीबोर्ड से अपना जवाब टाइप करें। सूची में संकेत पर क्लिक करके संबंधित वर्गों को हाइलाइट करें। अगले संकेत पर जाने के लिए टैब दबाएं, या सीधे किसी वर्ग पर क्लिक करें। अपने उत्तर जांचने के लिए चेक बटन का उपयोग करें — सही अक्षर हरे हो जाते हैं, गलत लाल। पहेली पूरी करने के लिए पूरा ग्रिड भरें।',
  },
  'slalom-canoe': {
    title: 'स्लैलम कैनो',
    description: `2012 ओलंपिक के इस Google Doodle में कैनो स्लैलम खेलें। गेट्स के बीच से नाव को तेज़ी से गुज़ारें।\n\nकैनो स्लैलम 1972 म्यूनिख खेलों से एक ओलंपिक खेल रहा है जिसमें एथलीटों को 250 मीटर के व्हाइटवॉटर कोर्स में 18 से 25 गेट्स से गुज़रना होता है। गेट पोल को छूने पर दो सेकंड का समय जुर्माना वास्तविक ओलंपिक नियमों को दर्शाता है।`,
    controls: 'बाएं पैडल मारने के लिए बायां एरो दबाकर रखें, और दाएं पैडल मारने के लिए दायां एरो। सीधे धारा के साथ पैडल मारने के लिए दोनों को बारी-बारी दबाएं। अपनी कैनो को हर गेट से गुज़ारें — हरे गेट नीचे की ओर जाते हुए पार करें, लाल गेट ऊपर की ओर। किसी पोल को छूने या गेट मिस करने पर समय जुर्माना जुड़ता है। जितनी जल्दी हो सके फिनिश लाइन पर पहुंचें।',
  },
  'eiji-tsuburayas-birthday': {
    title: 'Eiji Tsuburaya का जन्मदिन',
    description: `विशेष प्रभाव के जनक Eiji Tsuburaya के जन्मदिन Doodle में इंटरैक्टिव गेम खेलें।\n\n7 जुलाई, 2015 को सुबुराया की 114वीं जयंती पर जारी, यह डूडल 'टोकुसात्सु' शैली को श्रद्धांजलि देता है जिसे उन्होंने अग्रणी बनाया। 1954 की मूल गोडज़िला में सुबुराया के लघु सेट, सूटिमेशन और व्यावहारिक प्रभावों के नवाचारी उपयोग ने जापानी सिनेमा में क्रांति ला दी।`,
    controls: 'हर दृश्य शुरू करने के लिए क्लैपरबोर्ड पर क्लिक करें। फिर सही क्रम में ऑन-स्क्रीन बटन दबाकर सही समय पर स्पेशल इफेक्ट ट्रिगर करें — मॉन्स्टर को बुलाएं, विस्फोट ट्रिगर करें, और अभिनेताओं को निर्देशित करें। समय बहुत महत्वपूर्ण है: बहुत जल्दी या देर से क्लिक करने पर दृश्य बर्बाद हो जाता है। हर टेक से पहले निर्देशक के निर्देशों को ध्यान से देखें।',
  },
  'doodle-roswells-66th-anniversary': {
    title: 'Doodle Roswell की 66वीं वर्षगाँठ',
    description: `Roswell UFO घटना की 66वीं वर्षगाँठ के इस Google Doodle में एलियन की मदद से पज़ल सुलझाएँ।\n\n8 जुलाई, 2013 को 1947 की रोज़वेल घटना की 66वीं वर्षगाँठ पर जारी, यह Google के सबसे विस्तृत पॉइंट-एंड-क्लिक एडवेंचर्स में से एक है। तीन परस्पर जुड़े पहेली दृश्यों में क्लासिक LucasArts एडवेंचर गेम की याद दिलाने वाले रचनात्मक आइटम संयोजन की आवश्यकता होती है।`,
    controls: 'दृश्य में वस्तुओं और किरदारों पर क्लिक करके उनसे इंटरैक्ट करें। वस्तुएं उन पर क्लिक करके इकट्ठा करें — वे आपकी इन्वेंटरी में जाती हैं। इन्वेंटरी से वस्तुओं को संयोजित करने के लिए एक पर क्लिक करें फिर दूसरी पर। सुराग पाने वाली बातचीत के लिए लोगों पर क्लिक करें। एलियन को घर पहुंचाने के लिए आवश्यक सभी वस्तुएं और इंटरैक्शन खोजने के लिए हर दृश्य का हर हिस्सा खोजें।',
  },
  'google-maps-snake': {
    title: 'गूगल मैप्स स्नेक — साँप का खेल',
    description: `Google Maps पर क्लासिक स्नेक गेम खेलें। दुनिया भर के शहरों में यात्रियों को इकट्ठा करें।\n\n1 अप्रैल, 2019 को अप्रैल फूल सरप्राइज़ के रूप में जारी, Google Maps Snake सीधे iOS और Android पर Google Maps एप से खेलने योग्य था। छह उपलब्ध शहरों में से प्रत्येक में एफ़िल टॉवर से सिडनी ओपेरा हाउस तक के वास्तविक लैंडमार्क संग्रहणीय हैं।`,
    controls: 'शहर की गलियों में अपने सांप को चलाने के लिए एरो कीज़ या WASD का उपयोग करें। मैप पर दिखने वाले लैंडमार्क आइकन इकट्ठा करके लंबे होते जाएं और अंक प्राप्त करें। मैप के किनारों या अपनी पूंछ से टकराने से बचें। हर शहर में अलग-अलग लैंडमार्क इकट्ठा करने होते हैं। मोबाइल पर, जिस दिशा में मुड़ना हो उस दिशा में स्वाइप करें। शुरू करने से पहले मेनू से अपना शहर चुनें।',
  },
  'celebrating-pani-puri': {
    title: 'पानी पूरी उत्सव',
    description: `इस Google Doodle में पानी पूरी बनाएँ और सर्व करें। भारत के लोकप्रिय स्ट्रीट फ़ूड का मज़ा लें।\n\n12 जुलाई, 2023 को जारी यह डूडल पानी पूरी का जश्न मनाता है — जो पूरे दक्षिण एशिया में गोलगप्पा, फुचका और पानी के पताशे के नाम से भी जाना जाता है। इसकी उत्पत्ति कई भारतीय राज्यों के बीच विवादित है, लेकिन दुनिया के सबसे लोकप्रिय स्ट्रीट फ़ूड में से एक के रूप में इसकी स्थिति सर्वमान्य है।`,
    controls: 'पूरी को तोड़ने के लिए क्लिक करें या टैप करें, फिर ग्राहक के ऑर्डर में दिखाई गई सही भरावन सामग्री पर तुरंत टैप करें। पानी पूरी पूरी करने के लिए अंत में मसालेदार पानी पर टैप करें। ग्राहक का धैर्य खत्म होने से पहले उसे परोसें। हर ग्राहक अलग संयोजन चाहता है — उनका ऑर्डर ध्यान से पढ़ें और कतार बढ़ने पर तेज़ी से काम करें।',
  },
  'doodle-googles-15th-birthday': {
    title: 'Doodle Google की 15वीं सालगिरह',
    description: `Google की 15वीं सालगिरह के इस Doodle में पिनाटा तोड़ें और कैंडी इकट्ठा करें।\n\n27 सितंबर, 2013 को जारी यह डूडल 1998 में लैरी पेज और सर्गेई ब्रिन द्वारा स्टैनफोर्ड छात्रावास में Google की स्थापना की 15वीं वर्षगाँठ मनाता है। हर गिरता उपहार वर्षों में लॉन्च किए गए एक अलग Google उत्पाद या सुविधा का प्रतिनिधित्व करता है।`,
    controls: 'गिरते उपहारों के नीचे पिन्याटा को स्थित करने के लिए माउस बाएं-दाएं हिलाएं (या एरो कीज़ का उपयोग करें)। अंक प्राप्त करने के लिए रंगीन गिफ्ट बॉक्स पकड़ें। हानिकारक वस्तुओं से बचें — वे पिन्याटा को तोड़ देंगी और खेल समाप्त हो जाएगा। आपके स्कोर बढ़ने के साथ उपहार तेज़ी से गिरते हैं। पिन्याटा टूटने से पहले जितने उपहार हो सके पकड़ने की कोशिश करें।',
  },
  'swing-dancing-and-the-savoy-ballroom': {
    title: 'स्विंग डांसिंग और Savoy Ballroom!',
    description: `इस Google Doodle में स्विंग डांस का इतिहास जानें। Savoy Ballroom की जीवंत नृत्य संस्कृति का आनंद लें।\n\nSavoy Ballroom 1926 से 1958 तक हार्लेम, न्यूयॉर्क में संचालित था और संयुक्त राज्य अमेरिका में पहले नस्लीय रूप से एकीकृत सार्वजनिक स्थलों में से एक और लिंडी हॉप का जन्मस्थान था। यह डूडल पेशेवर स्विंग नर्तकों और इतिहासकारों के इनपुट से बनाया गया था।`,
    controls: 'संगीत की लय में हर डांस मूव करने के लिए स्क्रीन पर दिखाई गई कीज़ दबाएं। एरो कीज़ बुनियादी कदमों को नियंत्रित करती हैं, जबकि अक्षर कीज़ विशेष मूव ट्रिगर करती हैं। रिदम इंडिकेटर को ध्यान से देखें — स्विंग डांसिंग में टाइमिंग ही सब कुछ है। सटीक मूव मारकर अपना कॉम्बो मल्टीप्लायर बढ़ाएं। बहुत सारी बीट्स मिस होने पर भीड़ की दिलचस्पी कम हो जाती है।',
  },
  'doodle-qixi-festival-chilseok': {
    title: 'Doodle किक्सी फ़ेस्टिवल / चिलसोक',
    description: `इस Google Doodle में किक्सी और चिलसोक प्रेम उत्सव मनाएँ। तारों को जोड़कर पुल बनाएँ।\n\nज़िन्यू और नीउलांग की प्रेम कथा 2,600 वर्ष से अधिक पुरानी है और चीन, जापान, कोरिया और वियतनाम में मनाई जाती है। पौराणिक कथा के अनुसार, प्रेमियों को आकाशगंगा के विपरीत किनारों पर भेज दिया गया और वे साल में केवल एक बार मिल सकते हैं जब मैगपाइ पुल बनाती हैं।`,
    controls: 'मैगपाई को रात के आसमान में उड़ाने के लिए क्लिक करें या टैप करें। दो प्रेमियों को जोड़ने वाला पुल बनाने के लिए उड़ते हुए तारे इकट्ठा करें। आसमान में रास्ता बिगाड़ सकने वाली बाधाओं से बचें। मैगपाई उस दिशा में जाती है जहां आप क्लिक करते हैं — पर्याप्त तारे इकट्ठा करते हुए सुरक्षित रूप से आकाशगंगा पार करने के लिए अपना रास्ता ध्यान से तय करें।',
  },
  'chinese-new-year-snake-game': {
    title: 'चाइनीज़ न्यू ईयर — Snake गेम',
    description: `चीनी नव वर्ष के इस Google Doodle में विशेष स्नेक गेम खेलें। सर्प वर्ष का उत्सव मनाएँ।\n\nचीनी ज्योतिष में सर्प वर्ष ज्ञान, सुंदरता और अंतर्ज्ञान का प्रतीक है — जो क्लासिक स्नेक गेम को एक उत्तम विषयगत फिट बनाता है। लाल लिफाफे (होंगबाओ) सौभाग्य और समृद्धि का प्रतीक चंद्र नव वर्ष की एक प्रिय परंपरा हैं।`,
    controls: 'ड्रैगन सांप को त्योहारी बोर्ड पर चलाने के लिए एरो कीज़ या WASD का उपयोग करें। फॉर्च्यून कुकीज़ और लाल लिफाफे इकट्ठा करके लंबे होते जाएं और अंक प्राप्त करें। दीवारों या अपने शरीर से टकराने से बचें। सांप लगातार चलता रहता है — लंबा होने पर अपने मोड़ पहले से तय करें। मोबाइल पर, जिस दिशा में मुड़ना हो उस दिशा में स्वाइप करें।',
  },
  'doodle-celebrating-mbira': {
    title: 'Doodle Mbira उत्सव',
    description: `इस Google Doodle में अफ़्रीकी वाद्य यंत्र म्बीरा बजाएँ। सुंदर पारंपरिक धुनें बनाएँ।\n\nम्बीरा द्ज़ावाद्ज़िमु 1,000 वर्षों से शोना आध्यात्मिक समारोहों का केंद्र रहा है और 2020 में UNESCO ने इसे अमूर्त सांस्कृतिक विरासत के रूप में मान्यता दी। यह डूडल ज़िम्बाब्वे के म्बीरा उस्तादों के सहयोग से विकसित किया गया था ताकि धुनें और लयबद्ध पैटर्न प्रामाणिक हों।`,
    controls: 'स्क्रीन के नीचे म्बीरा की धातु की तानों की ओर उतरती रंगीन गेंदों पर नज़र रखें। जब गेंद तान तक पहुंचे तो कीबोर्ड पर संबंधित की दबाएं (या मोबाइल पर तान पर टैप करें)। टाइमिंग बहुत ज़रूरी है — परफेक्ट स्कोर के लिए बिल्कुल बीट पर हिट करें। बहुत सारे नोट्स मिस होने पर धुन बिगड़ जाती है। हर पारंपरिक शोना गीत में महारत हासिल करने के लिए पैटर्न सीखें।',
  },
  'hurdles-2012': {
    title: 'हर्डल्स 2012',
    description: `2012 ओलंपिक के इस Google Doodle में बाधा दौड़ में हिस्सा लें। कूदें और तेज़ी से फ़िनिश लाइन तक पहुँचें।\n\nयह डूडल 2012 लंदन ओलंपिक के लिए Google द्वारा बनाए गए इंटरैक्टिव मिनी-गेम श्रृंखला का हिस्सा था। यह अब तक के सबसे लोकप्रिय ओलंपिक डूडल में से एक बन गया जिसमें दुनिया भर के खिलाड़ी सबसे तेज़ समय के लिए प्रतिस्पर्धा कर रहे थे। तेज़ की-मैशिंग मैकेनिक ने इसे खेलों के दौरान एक पसंदीदा कार्यालय विकर्षण बना दिया।`,
    controls: 'अपने एथलीट को तेज़ दौड़ाने के लिए बाएं और दाएं एरो कीज़ को तेज़ी से बारी-बारी दबाएं। हर हर्डल पर कूदने के लिए ऊपर का एरो (या स्पेसबार) दबाएं। टाइमिंग ही सब कुछ है — बहुत जल्दी या देर से कूदने पर हर्डल से टकराएंगे और समय बर्बाद होगा। हर्डल के बीच अधिकतम गति बनाएं और दौड़ पूरी करने के लिए सभी 10 हर्डल पार करें।',
  },
  'soccer-2012': {
    title: 'सॉकर 2012',
    description: `2012 ओलंपिक के इस Google Doodle में गोलकीपर बनकर शॉट्स रोकें। सही समय पर गोता लगाएँ।\n\n2012 लंदन ओलंपिक Google डूडल श्रृंखला का हिस्सा, इस पेनल्टी शूटआउट गेम ने फुटबॉल के सबसे नाटकीय क्षण के रोमांच को कैप्चर किया। AI स्ट्राइकर राउंड बढ़ने के साथ पढ़ने में कठिन होते जाते हैं। यह Google के इतिहास में सबसे अधिक रीप्ले किए गए खेल डूडल में से एक बना हुआ है।`,
    controls: 'बाएं डाइव करने के लिए बायां एरो दबाएं, दाएं डाइव करने के लिए दायां एरो, या बीच में रहकर कूदने के लिए ऊपर का एरो। शूटर की दौड़ और शरीर की स्थिति देखकर अंदाज़ा लगाएं कि गेंद कहां जाएगी। गेंद लगने के बाद प्रतिक्रिया देने के लिए आपके पास एक पल होता है। राउंड में आगे बढ़ने के लिए जितनी हो सके उतनी पेनल्टी बचाएं।',
  },
  'global-candy-cup-2015': {
    title: 'Halloween ग्लोबल कैंडी कप 2015',
    description: `Halloween 2015 के इस Google Doodle में अपनी टीम चुनें और कैंडी इकट्ठा करके वैश्विक प्रतियोगिता जीतें।\n\nइस डूडल का Google इतिहास में विशेष स्थान है क्योंकि यह मोमो बिल्ली का पहला दिखावा था, जो बाद में लोकप्रिय मैजिक कैट अकादमी श्रृंखला में स्टार बनी। गेम में एक रीयल-टाइम ग्लोबल लीडरबोर्ड था और यह Google का पहला मल्टीप्लेयर प्रतिस्पर्धी हैलोवीन डूडल था।`,
    controls: 'अपनी चुड़ैल को उड़ाने और कैंडी इकट्ठा करने के लिए क्लिक करें या टैप करें। ऊंचाई एडजस्ट करने के लिए स्क्रीन पर ऊपर या नीचे क्लिक करके हॉन्टेड कोर्स से गुज़रें। बाधाओं से बचते हुए जितनी हो सके उतनी कैंडी इकट्ठा करें। फिनिश लाइन तक पहुंचने से पहले सबसे अधिक कैंडी इकट्ठा करने के लिए अन्य चुड़ैलों से प्रतिस्पर्धा करें। पावर-अप अस्थायी गति बढ़ाते हैं।',
  },
  'magic-cat-academy-2': {
    title: 'मैजिक कैट अकादमी 2 (Magic Cat Academy 2)',
    description: `बिल्ली Momo समुद्र के अंदर लौटती है! जादुई चिन्ह बनाकर पानी के नीचे के भूतों को हराएँ।\n\nमूल रूप से हैलोवीन 2020 में महामारी के दौरान जारी, इस सीक्वल ने मोमो को पूरी तरह जलीय साहसिक कार्य के लिए लहरों के नीचे ले जाया। विकास टीम ने भूत डिज़ाइन के लिए गहरे समुद्री जीवों से प्रेरणा ली और यह कई अलग-अलग पानी के नीचे के बायोम में फैला पहला हैलोवीन डूडल था।`,
    controls: 'हर आने वाले भूत के ऊपर दिखाए गए प्रतीक को क्लिक करके ड्रैग करके (या मोबाइल पर स्वाइप करके) बनाएं। भूत के मोमो तक पहुंचने से पहले प्रतीक सटीक रूप से मिलाएं। एक साथ कई भूत आ सकते हैं — सबसे करीब वालों को प्राथमिकता दें। बिना मिस किए प्रतीक श्रृंखला पूरी करने पर बोनस अंक मिलते हैं। तेज़ी से जटिल प्रतीक बनाकर अंतिम बॉस को हराएं।',
  },
  'magic-cat-academy-3': {
    title: 'मैजिक कैट अकादमी 3 (Magic Cat Academy 3)',
    description: `Magic Cat Academy की तीसरी कड़ी में Momo नई दुनिया की खोज करती है। जादू से दुश्मनों को हराएँ।\n\nहैलोवीन 2024 के लिए जारी, यह किस्त सबसे महत्वाकांक्षी मैजिक कैट अकादमी गेम है, जिसमें अद्वितीय दृश्य थीम वाली पांच पूर्ण दुनियाएं हैं। Google डूडल टीम ने विस्तारित कॉम्बो स्पेल सिस्टम और बहु-चरण बॉस मुकाबलों को विकसित करने में एक वर्ष से अधिक समय बिताया।`,
    controls: 'हर भूत के ऊपर दिखाए गए प्रतीक को क्लिक करके ड्रैग करके (या मोबाइल पर स्वाइप करके) बनाएं। इस संस्करण में नया: शक्तिशाली कॉम्बो स्पेल के लिए कई प्रतीकों को एक साथ जोड़ें। कुछ भूतों के लिए विशिष्ट प्रतीकों की आवश्यकता होती है — पैटर्न का अध्ययन करें ताकि पता चले कि कौन सा स्पेल किस दुश्मन को हराता है। मोमो का नवीनतम साहसिक कार्य पूरा करने के लिए सभी लहरों को हराकर अंतिम बॉस का सामना करें।',
  },
  'rubiks-cube': {
    title: 'Rubik\'s Cube',
    description: `Google Doodle में Rubik\'s Cube सुलझाएँ। रंगीन पहेली को घुमाकर सभी फलक मिलाएँ।\n\nयह डूडल एर्नो रूबिक के आविष्कार की 40वीं वर्षगाँठ मनाने के लिए बनाया गया था, जिसने 1974 में अपनी शुरूआत से अब तक 450 मिलियन से अधिक इकाइयां बेची हैं। Google का संस्करण 3D रोटेशन मैकेनिक्स को विश्वसनीय रूप से दोहराता है, जो इसे अब तक के सबसे तकनीकी रूप से प्रभावशाली इंटरैक्टिव डूडल में से एक बनाता है।`,
    controls: 'क्यूब की किसी पंक्ति या स्तंभ पर क्लिक करके ड्रैग करें ताकि वह परत घूमे। पूरे क्यूब को घुमाकर अलग-अलग साइड देखने के लिए बैकग्राउंड पर ड्रैग करें। क्यूब को मिलाने के लिए स्क्रैम्बल बटन क्लिक करें। रीसेट करने के लिए सॉल्व क्लिक करें। मोबाइल पर, किसी पंक्ति या स्तंभ पर स्वाइप करके उसे घुमाएं। आराम से करें — जब तक आप चुनौती न लें, कोई टाइमर नहीं है।',
  },
  'oskar-fischinger': {
    title: 'Oskar Fischinger म्यूज़िक टॉय',
    description: `Oskar Fischinger के इस Google Doodle में विज़ुअल म्यूज़िक बनाएँ। आकृतियाँ चुनें और अनोखी धुनें तैयार करें।\n\nफ़िशिंगर एक जर्मन-अमेरिकी एब्स्ट्रैक्ट एनिमेटर थे जिनके 1930-60 के दशक के अग्रणी कार्य ने दृश्य कला और संगीत के बीच के संबंध की खोज की। उनकी फ़िल्मों ने डिज़्नी की फैंटेसिया से लेकर आधुनिक म्यूज़िक विज़ुअलाइज़र तक को प्रेरित किया। यह डूडल सरल क्लिक को जीवंत ऑडियो-विज़ुअल कलाकृति में बदलता है।`,
    controls: 'ग्रिड में किसी सेल पर क्लिक करके नोट रखें — हर कॉलम लूप में एक बीट है, हर पंक्ति एक अलग पिच। भरे सेल को हटाने के लिए उस पर क्लिक करें। ध्वनि बदलने के लिए इंस्ट्रूमेंट सेलेक्टर का उपयोग करें। क्वांटाइज़्ड टाइमिंग के लिए स्नैप टू बीट और नोट्स को म्यूज़िकल स्केल में रखने के लिए कंस्ट्रेन टू की टॉगल करें। अपनी रचना लगातार लूप में सुनने के लिए प्ले दबाएं। शेयर बटन से अपनी रचना साझा करें।',
  },
  'celebrating-popcorn': {
    title: 'पॉपकॉर्न उत्सव',
    description: `इस Google Doodle में पॉपकॉर्न का जश्न मनाएँ। मक्के के दानों को पॉप करें और स्वादिष्ट पॉपकॉर्न बनाएँ।\n\nयह 2024 डूडल पॉपकॉर्न के समृद्ध इतिहास से प्रेरित था — पुरातात्विक साक्ष्य बताते हैं कि लोग 3600 ईसा पूर्व से मक्का पॉप कर रहे थे। मल्टीप्लेयर मोड ने इसे अब तक के सबसे सामाजिक Google डूडल में से एक बना दिया, जिसमें खिलाड़ी रीयल-टाइम लॉबी में प्रतिस्पर्धा करते थे।`,
    controls: 'पॉपकॉर्न दाने पॉप करने और अपनी बाल्टी भरने के लिए तेज़ी से क्लिक करें या टैप करें। तेज़ पॉपिंग या शील्ड जैसे अस्थायी फायदों के लिए स्क्रीन पर दिखने वाले पावर-अप इकट्ठा करें। अन्य खिलाड़ियों के उड़ते पॉपकॉर्न से बचें जो आपकी बाल्टी से दाने गिरा सकते हैं। जो खिलाड़ी अपनी बाल्टी पहले भरता है वह राउंड जीतता है। मोबाइल पर, अपनी पॉपिंग गति बढ़ाने के लिए जितना तेज़ हो सके टैप करें।',
  },
  'wewa-weaving': {
    title: 'We:wa बुनाई',
    description: `इस Google Doodle में पारंपरिक We:wa बुनाई कला सीखें। रंगीन धागों से सुंदर पैटर्न बनाएँ।\n\nWe:wa (1849-1896) ज़ूनी प्यूब्लो के एक उल्लेखनीय ल्हामाना (पारंपरिक तृतीय-लिंग भूमिका) थे जो सबसे कुशल बुनकरों में से एक बने। यह 2021 डूडल ज़ूनी सांस्कृतिक सलाहकारों के सहयोग से बनाया गया था ताकि बुनाई पैटर्न और तकनीकों का प्रामाणिक प्रतिनिधित्व सुनिश्चित हो सके।`,
    controls: 'हर पंक्ति बुनने के लिए धागे को लूम पर क्षैतिज रूप से क्लिक करके ड्रैग करें। किनारे पर पैलेट से अलग-अलग रंग के धागे चुनें। पारंपरिक ज़ूनी डिज़ाइन बनाने के लिए ऊपर दिखाए गए पैटर्न का पालन करें, या अपना खुद का बनाने के लिए उसे अनदेखा करें। नीचे से ऊपर की ओर पंक्ति दर पंक्ति काम करें। गलती होने पर अंतिम पंक्ति हटाने के लिए अनडू क्लिक करें। बुनाई पूरी करने के लिए पूरा पैटर्न पूरा करें।',
  },
  'rise-of-the-half-moon': {
    title: 'अर्धचंद्र का उदय',
    description: `इस Google Doodle इंटरैक्टिव कहानी में अर्धचंद्र के उदय का अनुभव करें। रात के आसमान की खोज करें।\n\nयह नवाचारी आवर्ती डूडल चंद्र चक्रों और दुनिया भर के सांस्कृतिक उत्सवों से जुड़ी नई सामग्री के साथ मासिक रूप से अपडेट होता है। कार्ड गेम मैकेनिक Google डूडल टीम द्वारा खगोल विज्ञान और विश्व पौराणिक कथाओं को सुलभ और आकर्षक बनाने के लिए डिज़ाइन किया गया था।`,
    controls: 'अपनी बारी पर, अपने हाथ से वह कार्ड चुनें जो टेबल पर मौजूदा कार्ड से जुड़ता हो — कार्ड में चंद्र चरण, रंग या श्रेणी समान होनी चाहिए। मिलते-जुलते कार्डों की श्रृंखला बनाकर बोनस अंक कमाएं। अतिरिक्त कार्ड पाने के लिए ट्रिविया प्रश्नों का सही उत्तर दें। डेक खत्म होने पर सबसे अधिक अंक वाला खिलाड़ी जीतता है। अपनी रणनीति बनाने के लिए चंद्र चरण कनेक्शन का अध्ययन करें।',
  },
  'zamboni': {
    title: 'Frank Zamboni का जन्मदिन',
    description: `Frank Zamboni के जन्मदिन के इस Google Doodle में आइस रिंक को ज़ैंबोनी मशीन से साफ़ करें।\n\nफ्रैंक ज़ैंबोनी ने 1949 में पैरामाउंट, कैलिफ़ोर्निया में पहली आइस रीसर्फेसिंग मशीन का आविष्कार किया, जिसने हमेशा के लिए आइस रिंक रखरखाव में क्रांति ला दी। उनके आविष्कार से पहले, रिंक को फिर से तैयार करने में श्रमिकों के एक दल और एक घंटे से अधिक समय लगता था — ज़ैंबोनी ने इसे मिनटों में कम कर दिया।`,
    controls: 'ज़ैम्बोनी को आइस रिंक पर चलाने के लिए एरो कीज़ का उपयोग करें। गंदी बर्फ के हर हिस्से पर चलकर उसे साफ करें — साफ किए गए क्षेत्र चमकीले सफेद हो जाते हैं। बिना कोई जगह छोड़े पूरी रिंक कवर करने के लिए अपना रास्ता ध्यान से प्लान करें। रिंक के किनारों से बाहर जाने से बचें। खेल तब समाप्त होता है जब आपने सारी बर्फ साफ कर दी हो या समय खत्म हो जाए। परफेक्ट स्कोर के लिए 100% कवरेज का लक्ष्य रखें।',
  },
  'komodo-national-park': {
    title: 'कोमोडो राष्ट्रीय उद्यान',
    description: `इस Google Doodle में Komodo National Park की खोज करें। कोमोडो ड्रैगन और अद्भुत वन्यजीवों के बारे में जानें।\n\nकोमोडो राष्ट्रीय उद्यान 1980 में इंडोनेशिया में स्थापित किया गया था और लगभग 3,000 कोमोडो ड्रैगन का घर है — पृथ्वी पर सबसे बड़ी जीवित छिपकलियां जो 3 मीटर तक बढ़ सकती हैं। डूडल के चित्र इंडोनेशियाई द्वीपसमूह की सुंदरता को कैप्चर करने के लिए हाथ से बनाए गए थे।`,
    controls: 'कोमोडो ड्रैगन के बारे में हर प्रश्न ध्यान से पढ़ें, फिर बहुविकल्पीय विकल्पों में से सही उत्तर पर क्लिक करें। आपका उत्तर सही था या गलत, इसकी तुरंत प्रतिक्रिया एक दिलचस्प तथ्य के साथ मिलती है। क्विज़ पूरी करने के लिए सभी प्रश्नों का उत्तर दें। हर प्रश्न का सही उत्तर देकर परफेक्ट स्कोर बनाने की कोशिश करें।',
  },
  'les-paul-guitar': {
    title: 'लेस पॉल गिटार डूडल',
    description: `Les Paul के इस Google Doodle में वर्चुअल गिटार बजाएँ। तार छेड़ें और अपनी धुनें रिकॉर्ड करें।\n\nलेस पॉल (1915-2009) न केवल एक महान गिटारवादक थे बल्कि मल्टीट्रैक रिकॉर्डिंग और सॉलिड-बॉडी इलेक्ट्रिक गिटार के अग्रणी भी थे। 2011 का यह डूडल इतना लोकप्रिय हुआ कि Google ने इसे अतिरिक्त 48 घंटे लाइव रखा। उपयोगकर्ताओं ने पहले 48 घंटों में 40 मिलियन से अधिक घंटे इसे बजाने में बिताए।`,
    controls: 'गिटार के तारों पर माउस घुमाकर स्ट्रम करें, या अलग-अलग तार पर क्लिक करके नोट बजाएं। आप कीबोर्ड का भी उपयोग कर सकते हैं — हर की अलग नोट बजाती है। अपने प्रदर्शन का 30 सेकंड का क्लिप कैप्चर करने के लिए रिकॉर्ड बटन दबाएं। अलग-अलग धुनें आज़माएं और अपनी संगीत रचना साझा करें।',
  },
  'robert-moog-synthesizer': {
    title: 'रॉबर्ट मूग सिंथेसाइज़र डूडल',
    description: `Robert Moog के इस Google Doodle में सिंथेसाइज़र बजाएँ। नॉब्स घुमाएँ और इलेक्ट्रॉनिक ध्वनियाँ बनाएँ।\n\nरॉबर्ट मूग (1934-2005) ने पहला व्यावसायिक रूप से उपलब्ध सिंथेसाइज़र बनाकर संगीत में क्रांति ला दी, जिसने वेंडी कार्लोस से क्राफ्टवर्क तक कलाकारों की ध्वनि को आकार दिया। यह डूडल प्रतिष्ठित Minimoog Model D के इंटरफ़ेस को विश्वसनीय रूप से पुनर्निर्मित करता है।`,
    controls: 'नोट बजाने के लिए नीचे कीबोर्ड कीज़ पर क्लिक करें। ऑसिलेटर वेवफॉर्म, फिल्टर कटऑफ, रेज़ोनेन्स और एनवेलप सेटिंग्स बदलने के लिए ऊपर के नॉब और स्विच एडजस्ट करें। दाईं ओर 4-ट्रैक रिकॉर्डर से ध्वनियां लेयर करें — रिकॉर्ड दबाएं, अपनी धुन बजाएं, फिर और लेयर जोड़ने के लिए ट्रैक बदलें। अपनी पूरी रचना सुनने के लिए प्ले क्लिक करें।',
  },
  'alan-turing-machine': {
    title: 'एलन ट्यूरिंग मशीन डूडल',
    description: `Alan Turing के इस Google Doodle में ट्यूरिंग मशीन की पहेलियाँ सुलझाएँ। लॉजिक और कोडिंग चुनौतियाँ पूरी करें।\n\nएलन ट्यूरिंग (1912-1954) को सैद्धांतिक कंप्यूटर विज्ञान और कृत्रिम बुद्धिमत्ता का जनक माना जाता है। ट्यूरिंग मशीन की उनकी अवधारणा — गणना का एक गणितीय मॉडल — ने हर आधुनिक कंप्यूटर की नींव रखी। यह डूडल जटिल कंप्यूटेशनल सिद्धांत को सुलभ पहेलियों में बदलता है।`,
    controls: 'हर पहेली में ऊपर एक लक्ष्य बाइनरी अनुक्रम दिखाया जाता है। ट्यूरिंग मशीन टेप पढ़ती है और आपके प्रोग्राम किए गए नियमों का पालन करती है। क्रियाओं को बदलने के लिए स्टेट टेबल सेल पर क्लिक करें (0 लिखें, 1 लिखें, बाएं चलें, दाएं चलें, स्टेट बदलें)। अपना प्रोग्राम चलाने और आउटपुट लक्ष्य से मेल खाता है या नहीं देखने के लिए प्ले बटन दबाएं। डूडल पूरा करने के लिए सभी लेवल हल करें।',
  },
  'pangolin-love': {
    title: 'Pangolin Love — वैलेंटाइन डे 2017',
    description: `वैलेंटाइन डे 2017 के इस Google Doodle में पैंगोलिन को उसके साथी तक पहुँचाएँ। सामग्री इकट्ठा करके प्यार भरा उपहार बनाएँ।\n\nपैंगोलिन दुनिया के सबसे अधिक तस्करी किए जाने वाले स्तनधारी हैं, जो इस वैलेंटाइन डे डूडल को आकर्षक और सार्थक दोनों बनाता है। चार स्तर घाना, भारत, चीन और फिलीपींस में स्थापित हैं — विभिन्न पैंगोलिन प्रजातियों के प्राकृतिक आवासों का प्रतिनिधित्व करते हुए। Google ने वन्यजीव संरक्षण संगठनों के साथ साझेदारी की।`,
    controls: 'पैंगोलिन को कूदाने और वातावरण से इंटरैक्ट करने के लिए क्लिक करें या टैप करें। हर लेवल में बिखरी चॉकलेट, फूल और दिल जैसी प्रेम-थीम वस्तुएं इकट्ठा करें। अपने रास्ते की बाधाओं और खतरों से बचें। पैंगोलिन को एक परफेक्ट वैलेंटाइन डे सरप्राइज़ देने में मदद करने के लिए चारों लेवल पूरे करें — हर एक अलग देश में।',
  },
  'santa-tracker-elf-maker': {
    title: 'सांता ट्रैकर — एल्फ़ मेकर',
    description: `Google Santa Tracker में अपना खुद का एल्फ़ बनाएँ। कपड़े, रंग और एक्सेसरीज़ चुनकर कस्टमाइज़ करें।\n\nGoogle का सांता ट्रैकर 2004 से एक प्रिय छुट्टी परंपरा रहा है और एल्फ़ मेकर इसकी सबसे लोकप्रिय गतिविधियों में से एक है। कैरेक्टर क्रिएटर हजारों संभावित संयोजन प्रदान करता है, यह सुनिश्चित करता है कि कोई भी दो एल्फ़ एक जैसे न हों। बनाए गए एल्फ़ को प्रोफ़ाइल अवतार या हॉलिडे ग्रीटिंग कार्ड के रूप में साझा किया जा सकता है।`,
    controls: 'नीचे कस्टमाइज़ेशन श्रेणियों पर क्लिक करके आगे बढ़ें — सिर, शरीर, एक्सेसरीज़ और रंग। अलग-अलग आइटम पर क्लिक करके उन्हें अपने एल्फ पर लगाएं। हर श्रेणी में अधिक विकल्प ब्राउज़ करने के लिए एरो बटन का उपयोग करें। सरप्राइज़ संयोजन के लिए रैंडमाइज़ बटन क्लिक करें। अपने एल्फ से खुश होने पर, उसे सेव या भेजने के लिए शेयर बटन क्लिक करें।',
  },
  'santa-tracker-snowball-storm': {
    title: 'सांता ट्रैकर — बर्फ़ का तूफ़ान',
    description: `Google Santa Tracker में बर्फ़ के गोलों की लड़ाई खेलें। एल्फ़ों पर निशाना लगाएँ और स्नोबॉल फ़ाइट जीतें।\n\nयह एक्शन-पैक्ड मिनी-गेम Google के सांता ट्रैकर संग्रह की अधिक प्रतिस्पर्धी प्रविष्टियों में से एक है। भौतिकी इंजन यथार्थवादी स्नोबॉल आर्क और प्रभाव प्रभाव बनाता है। विशाल स्नोबॉल और रैपिड फायर जैसे पावर-अप एक सरल थ्रोइंग गेम में रणनीतिक गहराई जोड़ते हैं।`,
    controls: 'स्नोबॉल का निशाना लगाने के लिए माउस हिलाएं। प्रतिद्वंद्वियों पर स्नोबॉल फेंकने के लिए क्लिक करें। आने वाले स्नोबॉल से बचने के लिए नीचे जाकर अपने स्नो फोर्ट के पीछे छिपें। बेहतर थ्रो के लिए दिखने वाले पावर-अप आइटम इकट्ठा करें — विशाल स्नोबॉल, रैपिड फायर, और बहुत कुछ। बहुत ज़्यादा हिट खाए बिना जितनी हो सके उतनी लहरों में टिके रहें।',
  },
  'santa-tracker-present-drop': {
    title: 'सांता ट्रैकर — उपहार गिराओ',
    description: `Google Santa Tracker में सांता की स्लेज से तोहफ़े गिराएँ। सही चिमनी में उपहार पहुँचाएँ।\n\nप्रेज़ेंट ड्रॉप क्लासिक बॉम्बिंग-रन आर्केड गेम का एक आधुनिक रूप है, जिसे उत्सवी हॉलिडे आकर्षण के साथ पुनर्कल्पित किया गया है। शुरुआती पड़ोस धीमे चलते हैं जिससे आप टाइमिंग सीख सकें। उच्च स्कोर के लिए चिमनी पैटर्न याद रखना और अपने ड्रॉप की लय विकसित करना आवश्यक है।`,
    controls: 'सांता की स्लेज अपने आप स्क्रीन पर उड़ती है। उपहार गिराने के लिए क्लिक करें या स्पेस दबाएं। अपने ड्रॉप का समय ऐसे रखें कि उपहार सीधे नीचे की चिमनियों में गिरें। हर सफल डिलीवरी पर अंक मिलते हैं — लगातार डिलीवरी पर बोनस अंक। आगे बढ़ने पर स्लेज तेज़ हो जाती है। बहुत सारी चिमनियां मिस होने पर खेल समाप्त हो जाता है।',
  },
  'santa-tracker-penguin-dash': {
    title: 'सांता ट्रैकर — पेंगुइन डैश',
    description: `Google Santa Tracker में पेंगुइन दौड़ खेलें। बर्फ़ पर फिसलते हुए तेज़ी से आगे बढ़ें।\n\nयह एंडलेस रनर क्लासिक साइड-स्क्रॉलिंग गेम से प्रेरित है जिसमें फिसलन भरी बर्फ भौतिकी है जो गति को विशिष्ट रूप से पेंगुइन जैसा बनाती है। प्रक्रियात्मक रूप से उत्पन्न कोर्स सुनिश्चित करते हैं कि कोई दो दौड़ एक जैसी न हों। यह सांता ट्रैकर संग्रह में सबसे परिवार-अनुकूल गेम में से एक है।`,
    controls: 'अपने पेंगुइन को नियंत्रित करने के लिए एरो कीज़ का उपयोग करें या स्क्रीन टैप करें। बाधाओं पर कूदने के लिए ऊपर या स्पेस दबाएं। बैरियर के नीचे से स्लाइड करने के लिए नीचे दबाएं। बोनस अंकों के लिए कैंडी केन और हॉलिडे ट्रीट इकट्ठा करें। बाधाओं से टकराने से बचें — बहुत ज़्यादा हिट खाने पर गति धीमी हो जाती है। जितनी जल्दी हो सके फिनिश लाइन पर पहुंचें।',
  },
  'santa-tracker-code-lab': {
    title: 'सांता ट्रैकर — कोड लैब',
    description: `Google Santa Tracker में कोडिंग सीखें। ब्लॉक्स जोड़कर एल्फ़ को डांस करना सिखाएँ।\n\nGoogle के Blockly विज़ुअल प्रोग्रामिंग फ्रेमवर्क पर निर्मित, कोड लैब अनुक्रमण, लूप और कंडीशनल जैसी मौलिक प्रोग्रामिंग अवधारणाओं को सुलभ हॉलिडे सेटिंग में प्रस्तुत करता है। इसे दुनिया भर के शिक्षकों ने छुट्टियों के मौसम में कंप्यूटेशनल थिंकिंग के मज़ेदार परिचय के रूप में उपयोग किया है।`,
    controls: 'टूलबॉक्स से कोड ब्लॉक्स को वर्कस्पेस में ड्रैग करें। ब्लॉक्स को एक साथ जोड़कर निर्देशों का अनुक्रम बनाएं। एल्फ को मार्गदर्शन करने के लिए मूव ब्लॉक, क्रियाओं को दोहराने के लिए लूप ब्लॉक, और निर्णय लेने के लिए कंडीशनल ब्लॉक का उपयोग करें। अपना कोड चलाने के लिए रन बटन क्लिक करें। एल्फ को अपने निर्देशों का पालन करते हुए देखें। ज़रूरत पड़ने पर अपना कोड एडजस्ट करें और फिर से कोशिश करें।',
  },
  'chrome-music-lab-rhythm': {
    title: 'Chrome Music Lab — रिदम गेम',
    description: `Chrome Music Lab में रिदम गेम खेलें। बीट्स बनाएँ और संगीत की लय को समझें।\n\nChrome Music Lab Google की Creative Lab ने संगीतकारों और कोडर्स के सहयोग से बनाया था ताकि संगीत सीखना अधिक सुलभ हो। रिदम प्रयोग विशेष रूप से उपयोगकर्ताओं को टाइम सिग्नेचर, पॉलीरिदम और विभिन्न पर्कशन पैटर्न की बातचीत समझने में मदद करता है। इसे दुनिया भर की संगीत कक्षाओं में शिक्षण उपकरण के रूप में उपयोग किया जाता है।`,
    controls: 'बीट रखने के लिए ग्रिड सेल पर क्लिक करें। हर पंक्ति एक अलग पर्कशन ध्वनि दर्शाती है। बीट चालू या बंद करने के लिए सेल पर क्लिक करें। प्लेहेड लगातार ग्रिड पर चलता है और जो भी बीट मिले उसे बजाता है। गति स्लाइडर से टेम्पो एडजस्ट करें। नीचे स्क्रॉल करके और इंस्ट्रूमेंट जोड़ें। नए सिरे से शुरू करने के लिए ग्रिड साफ करें या जटिल रिदम के लिए लेयर करते रहें।',
  },
  'chrome-music-lab-song-maker': {
    title: 'क्रोम म्यूज़िक लैब — सॉन्ग मेकर',
    description: `Chrome Music Lab में अपना खुद का गाना बनाएँ। नोट्स और बीट्स चुनकर अनोखा संगीत तैयार करें।\n\nSong Maker सबसे लोकप्रिय Chrome Music Lab प्रयोग बन गया है, जिसे दुनिया भर में लाखों शिक्षक और छात्र संगीत शिक्षा के लिए उपयोग करते हैं। इसका रंग-कोडित ग्रिड सीधे संगीत स्केल पर मैप होता है, जो संगीत सिद्धांत को शुरुआती लोगों के लिए दृश्य और सहज बनाता है।`,
    controls: 'मेलोडी नोट्स रखने के लिए ऊपरी ग्रिड पर क्लिक करें — हर कॉलम एक बीट है, हर पंक्ति एक पिच। ड्रम बीट जोड़ने के लिए नीचे के ग्रिड पर क्लिक करें। अपनी रचना सुनने के लिए प्ले बटन दबाएं। ध्वनि बदलने के लिए इंस्ट्रूमेंट सेलेक्टर का उपयोग करें। सेटिंग्स से टेम्पो और की एडजस्ट करें। ध्वनि रिकॉर्ड करने के लिए माइक्रोफोन क्लिक करें। जनरेटेड लिंक से अपना गाना सेव और शेयर करें।',
  },
  'chrome-music-lab-kandinsky': {
    title: 'क्रोम म्यूज़िक लैब — कैंडिंस्की',
    description: `Chrome Music Lab में Kandinsky जैसी कला बनाएँ। चित्र बनाएँ और उन्हें संगीत में बदलते देखें।\n\nवासिली कैंडिंस्की (1866-1944) एक रूसी चित्रकार और कला सिद्धांतकार थे जो दृश्य कला और संगीत के बीच गहरे संबंध में विश्वास करते थे, जिसे सिनेस्थीसिया कहा जाता है। यह प्रयोग आकृतियों, रंगों और स्थितियों को संगीत तत्वों से जोड़कर उनके दर्शन को जीवंत करता है।`,
    controls: 'माउस या उंगली से कैनवास पर ड्रॉ करें। हर स्ट्रोक ध्वनि बनाता है — रेखाएं लंबी टोन बनाती हैं, वृत्त पर्कसिव बीट बनाते हैं, और त्रिकोण तीखे एक्सेंट जोड़ते हैं। अलग-अलग रंग अलग-अलग इंस्ट्रूमेंट से जुड़े हैं। अपनी ड्रॉइंग को बाएं से दाएं संगीत के रूप में सुनने के लिए प्ले बटन दबाएं। नई रचनाओं के लिए कैनवास साफ करें और फिर से ड्रॉ करें।',
  },
  'quick-draw': {
    title: 'Quick, Draw! गूगल का',
    description: `क्या एक न्यूरल नेटवर्क आपके स्केच पहचानना सीख सकता है? Quick, Draw! गूगल का AI प्रयोग है जो आपको 20 सेकंड में रोज़मर्रा की वस्तुएं बनाने की चुनौती देता है।

Quick, Draw! को गूगल के न्यूरल नेटवर्क को हाथ से बनाई गई छवियों की पहचान में प्रशिक्षित करने के लिए बनाया गया था।`,
    controls: `आपको एक शब्द दिया जाता है और माउस या उंगली से इसे बनाने के लिए 20 सेकंड मिलते हैं। टाइमर शुरू होते ही बनाना शुरू करें — AI रीयल टाइम में आपके स्ट्रोक का विश्लेषण करता है। कुल छह राउंड।`,
  },
  'google-earth-flight-simulator': {
    title: 'Earth — हवा और मौसम मानचित्र',
    description: `एक सुंदर एनिमेटेड 3D ग्लोब पर पूरे ग्रह का अन्वेषण करें जो रीयल-टाइम हवा के पैटर्न, मौसम की स्थिति और समुद्री धाराओं को विज़ुअलाइज़ करता है।

हर तीन घंटे अपडेट होने वाले सुपरकंप्यूटर पूर्वानुमानों द्वारा संचालित। कैमरन बेकारियो द्वारा बनाया गया।`,
    controls: `ग्लोब घुमाने के लिए क्लिक करें और खींचें। ज़ूम करने के लिए स्क्रॉल करें। हवा की गति, तापमान और निर्देशांक देखने के लिए कहीं भी क्लिक करें। वायुमंडलीय परतों के बीच स्विच करने के लिए मेनू का उपयोग करें।`,
  },
};
